[Form]
public class EInvoiceParameters_IT extends FormRun
{
    boolean runExecuteDirect;
    TmpIdRef tmpIdRef;

    NumberSeqScope scope;
    NumberSeqApplicationModule numberSeqApplicationModule;
    container numberSequenceModules;

}

public void enableFields()
{
    boolean taxRepNoLegalEntity = !eInvoiceParameters_IT.TaxRepIsCompany && eInvoiceParameters_IT.IsTaxRep;

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TrnCountryCode)).allowEdit      (!eInvoiceParameters_IT.TrnIsFilingForSameLegalEntity);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TrnTaxExempNumber)).allowEdit   (!eInvoiceParameters_IT.TrnIsFilingForSameLegalEntity);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TrnEmail)).allowEdit            (!eInvoiceParameters_IT.TrnIsFilingForSameLegalEntity);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TrnPhone)).allowEdit            (!eInvoiceParameters_IT.TrnIsFilingForSameLegalEntity);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepCountryCode)).allowEdit   (eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepTaxExempNumber)).allowEdit(eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepFiscalCode)).allowEdit    (eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepIsCompany)).allowEdit     (eInvoiceParameters_IT.IsTaxRep);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepCompanyDesignation)).allowEdit(!taxRepNoLegalEntity);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepFirstName)).allowEdit     (eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepLastName)).allowEdit      (eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepTitle)).allowEdit         (eInvoiceParameters_IT.IsTaxRep);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepCodeEORI)).allowEdit      (eInvoiceParameters_IT.IsTaxRep);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepCompanyDesignation)).allowEdit(eInvoiceParameters_IT.TaxRepIsCompany);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepFirstName)).allowEdit     (taxRepNoLegalEntity);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepLastName)).allowEdit      (taxRepNoLegalEntity);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, TaxRepTitle)).allowEdit         (taxRepNoLegalEntity);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, GenIsAutoDigitalSigning)).allowEdit(eInvoiceParameters_IT.GenDigitalSignature != DigitalSignature::Disabled);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ArtProvince)).allowEdit         (eInvoiceParameters_IT.IsArt2250);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ArtRegistrationNum)).allowEdit  (eInvoiceParameters_IT.IsArt2250);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ArtShareCapital)).allowEdit     (eInvoiceParameters_IT.IsArt2250);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ArtSoleShareholder)).allowEdit  (eInvoiceParameters_IT.IsArt2250);
    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ArtLiquidation)).allowEdit      (eInvoiceParameters_IT.IsArt2250);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, EInvoiceSignerParty_ES)).allowEdit  (eInvoiceParameters_IT.EInvoiceSignerType_ES == EInvoiceSignerType_ES::ThirdParty);

    eInvoiceParameters_IT_ds.object(fieldnum(EInvoiceParameters_IT, ERValidationFormatMapping)).allowEdit(eInvoiceParameters_IT.DoValidateEInvoice);
}

public void init()
{
    CompanyInfo companyinfo = companyinfo::find(); 

    ttsbegin;

    EInvoiceParameters_IT eInvoiceParameters_ITLoc = EInvoiceParameters_IT::find(true);

    if (eInvoiceParameters_ITLoc)
    {
        EInvoiceParameters_IT eInvoiceParameters_ITOrgnl =  eInvoiceParameters_ITLoc.data();
        eInvoiceParameters_ITLoc.CoRegNum = companyinfo.CoRegNum;
        eInvoiceParameters_ITLoc.Name = companyinfo.name;
        eInvoiceParameters_ITLoc.Phone = CompanyInfo.phone();
        eInvoiceParameters_ITLoc.Email = companyInfo.email();
        eInvoiceParameters_ITLoc.TeleFax = companyInfo.teleFax();
        eInvoiceParameters_ITLoc.FiscalCode_IT = companyinfo.FiscalCode_IT;
        if (!bufCmp(eInvoiceParameters_ITLoc, eInvoiceParameters_ITOrgnl))
        {
            eInvoiceParameters_ITLoc.update();
        }
    }

    ttscommit;

    this.numberSeqPreInit();

    super();

    this.enableKeyVaultFields();

    EInvoiceParameters_IT::find();

    GeneralTab.visible(!BusinessDocumentParameters::isFeatureEnabled(EInvoicingFeaturesConstants::ExportInvoicesFeaturesFatturaPA));
}

public void enableKeyVaultFields()
{
    boolean useAdvCert = SystemParameters::find().UseAdvCertificateStore;

    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, UserNameKeyVaultRef)).visible(useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, UserPasswordKeyVaultRef)).visible(useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, ClientIdKeyVaultRef)).visible(useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, ClientSecretKeyVaultRef)).visible(useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, KeyVaultCertificateRef)).visible(useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, UserName)).visible(!useAdvCert);
    GeneralTabFieldGroupEInvoice_IN_UserPassword.visible(!useAdvCert);
    GeneralTabFieldGroupEInvoice_IN_ClientId.visible(!useAdvCert);
    GeneralTabFieldGroupEInvoice_IN_ClientSecret.visible(!useAdvCert);
    eInvoiceParameters_IT_ds.object(fieldNum(EInvoiceParameters_IT, CertificateSubject)).visible(!useAdvCert);
}

void numberSeqPreInit()
{
    runExecuteDirect   = false;

    numberSequenceModules = [ NumberSeqModule::EInvoice ];
    numberSeqApplicationModule = new NumberSeqModuleEInvoice();
    scope = NumberSeqScopeFactory::createDataAreaScope();

    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

public int active()
{
    int ret = super();

    element.enableFields();

    return ret;
}

