void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;
        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                             tmpIdRef,
                                                             numberSequenceTable,
                                                             numberSequenceModules,
                                                             scope));
        numbersequenceReference_ds.research();
    }
}
