public void enableCFDI(boolean _enable = true)
{
    if (!_enable)
    {
        EInvoiceCFDIParameters.EnabledCFDI = NoYes::No;
    }

    EInvoiceEnabled_EnabledCFDI.enabled(_enable);
}

public void enableCFDIFields(boolean _enable = true)
{
    DictTable       eInvoiceParam;
    DictField       eInvoiceField;
    int             fieldId;

    eInvoiceParam = new DictTable(tableNum(EinvoiceCFDIParameters_MX));

    CFDI_CFDIVersion.enabled(_enable);

    if (eInvoiceParam)
    {
        fieldId = eInvoiceParam.fieldNext(fieldId);
        while (fieldId)
        {
            eInvoiceField = eInvoiceParam.fieldObject(fieldId);
            if (!eInvoiceField.isSystem() && fieldId != fieldnum(EInvoiceCFDIParameters_MX, EnabledCFDI))
            {
                EInvoiceCFDIParameters_DS.object(fieldId).enabled(_enable);
            }

            fieldId = eInvoiceParam.fieldNext(fieldId);
        }
    }

    this.enableCFDIWithholdingFields();
}

public void enableCFDIWithholdingFields()
{
    boolean enable = EInvoiceCFDIParameters.EnabledCFDIWithholding && EInvoiceCFDIParameters.EnabledCFDI;

    CFDIWithholdingParams.enabled(enable);
    CFDIWithholdingEmail.enabled(enable);
}

public void enableModified()
