public void setup(int curValue)
{
    int curValueIndex = 0;
    cfdiVersionIndexes = new Map(Types::Integer, Types::Integer);

    SysDictEnum dictEnum = new SysDictEnum(enumnum(EInvoiceCFDIVersion_MX));
    Set removeValues = new Set(Types::Enum);

    removeValues.add(EInvoiceCFDIVersion_MX::V3);

    int enumCount = 0;

    for (int i = 0; i < dictEnum.values(); i++)
    {
        int value = dictEnum.index2Value(i);
        if (!removeValues.in(value))
        {
            this.add(dictEnum.index2Label(i));
            cfdiVersionIndexes.insert(enumCount, value);

            if (value == curValue)
            {
                curValueIndex = enumCount;
            }

            enumCount++;
        }
    }

    CFDI_CFDIVersion.selection(curValueIndex);
}

public int selectionChange()
{
    int ret;

    ret = super();

    int selectedValue = cfdiVersionIndexes.lookup(CFDI_CFDIVersion.selection());

    EInvoiceCFDIParameters_DS.object(fieldnum(EInvoiceCFDIParameters_MX, CFDIVersion)).setValue(selectedValue);

    return ret;
}

