public void showFields()
{
    boolean readyForCancel = EInvoice_IN.Status == EInvoiceStatus_IT::SendOK;
    CancelButton.enabled(readyForCancel);

    boolean readyForEWBCancel = EInvoice_IN.EWayBillStatus == EInvoiceEWayBillStatus_IN::Sent;
    CancelEWBButton.enabled(readyForEWBCancel);

    SourceCustInvoiceJournal.visible(EInvoice_IN.RefTableId == tableNum(CustInvoiceJour));
    SourceProjInvoiceJournal.visible(EInvoice_IN.RefTableId == tableNum(ProjInvoiceJour));
    SourceInventTransferJour.visible(EInvoice_IN.RefTableId == tableNum(InventTransferJour));
    SourceShippingBillJournal.visible(EInvoice_IN.RefTableId == tableNum(CustomsShippingBillJour_IN));
}

public void run()
{
    super();
    Args args = this.args();

    if (args && args.record())
    {
        ExtendedGridGroup.visible(false);
        FileIntegrationMenuButton.visible(false);
    }
}

public int active()
{
    int ret = super();

    element.showFields();

    return ret;
}

public void clicked()
{
    super();

    if (Box::yesNo("@Tax_INOnlineInvoicing:EInvoiceCancelDialog", DialogButton::No) == DialogButton::Yes)
    {
        EInvoice_IN eInvoice = eInvoice_IN_ds.cursor() as EInvoice_IN;
        eInvoice.Status = EInvoiceStatus_IT::CancelRequest;
        CancelButton.enabled(false);
    }
}

public void clicked()
{
    super();

    if (Box::yesNo("@Tax_INOnlineInvoicing:EWBCancelDialog", DialogButton::No) == DialogButton::Yes)
    {
        EInvoice_IN eInvoice = eInvoice_IN_ds.cursor() as EInvoice_IN;
        eInvoice.EWayBillStatus = EInvoiceEWayBillStatus_IN::CancelRequest;
        CancelEWBButton.enabled(false);
    }
}
