[Form]
public final class EUSalesList extends FormRun
{
    #resAppl
    #ISOCountryRegionCodes
    Num                     num;
    NumberSeq               numberSeq;
    Counter                 seqNumCounter;
    ListDispatchId          allocatedReportNum;

    QueryBuildRange         invoiceId;
    QueryBuildRange         invoiceDate;

    QueryBuildRange         selectListStatusRange;

}

void init()
{
    super();

    TaxVATNumTable::enableLookupVATNum(euSalesList_VatNum);

    euSalesList_Direction.visible(IntrastatParameters::find().euSalesListTransferPurchases);

    if (this.supportsCorrection())
    {
        euSalList_Corrected.label("@SYS15649");
        EUSalesList_Corrected_Overview.visible(false);
    }
    else
    {
        // Correction tab should be visible only if there are more controls then just Correction checkbox
        euSalList_Corrected.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU])
            || EUSalesListByTaxId_BEToggle::isBECountryRegionORMultipleTaxIdEUSalesListEnabled());
    }

    this.showTaxSolutionScopeFields(TaxSolutionScopeIntegrationUtil::isCompanyEnabled());

    if (TaxIntegrationUtils::isMultipleTaxIdEnabledForEUSalesList())
    {
        Transfer.visible(false);
        EUSalesListReporting.visible(false);
        MarkIncluded.visible(false);
        MarkReported.visible(false);
        MarkClosed.visible(false);
        EUSalesList_VATNum.label("@EUSalesListCommon:CounterpartyTaxRegistrationNumber");
    }
}

private void showTaxSolutionScopeFields(boolean _isCompanyEnabled)
{
    EUSalesListReporting.visible(!_isCompanyEnabled);
    ElectronicDeclaration.visible(_isCompanyEnabled);
}

public void showHideTotals()
{
    select firstonly custInvoiceJour
        where custInvoiceJour.InvoiceId         == euSalesList.Invoice
           && custInvoiceJour.InvoiceAccount    == euSalesList.AccountNum
           && custInvoiceJour.Listcode          == euSalesList.Listcode
           && custInvoiceJour.InvoiceDate       == euSalesList.TransDate;

    if (euSalesList.Direction == ModuleSalesPurch::Purch)
    {
        custInvoiceJournalTotals.enabled(false);
        projInvoiceJournalTotals.enabled(false);
    }
    else
    {
        if (custInvoiceJour)
        {
            custInvoiceJournalTotals.visible(true);
            custInvoiceJournalTotals.enabled(true);
            custInvoiceJournalTotals.text("@SYS8311");

            if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
            {
                projInvoiceJournalTotals.visible(false);
            }
        }
        else
        {
            custInvoiceJournalTotals.visible(false);

            if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
            {
                projInvoiceJournalTotals.visible(true);
                projInvoiceJournalTotals.enabled(true);
                projInvoiceJournalTotals.text("@SYS8311");
            }
        }
    }
}

public void updateCorrectionControls()
{
    if (this.supportsCorrection())
    {
        //update item corrections controls
        if (euSalesList.Corrected)
        {
            this.updateCorrectionGroupControls(correctionItems, euSalList_Corrected.id(), true);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionYear)).mandatory(true);
            euSalesList_ds.object(fieldNum(EUSalesList, TaxRep349AmountMstPrev)).mandatory(true);

            if (euSalesList.CorrectionPeriod == EUSalesListCorrectionPeriod_ES::Month
                || euSalesList.CorrectionPeriod == EUSalesListCorrectionPeriod_ES::Quarter)
            {
                correctionItems_CorrectionMonthOrQuarter.enabled(true);
                euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarter)).mandatory(true);
            }
            else
            {
                correctionItems_CorrectionMonthOrQuarter.enabled(false);
                euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarter)).mandatory(false);
            }
        }
        else
        {
            this.updateCorrectionGroupControls(correctionItems, euSalList_Corrected.id(), false);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionYear)).mandatory(false);
            euSalesList_ds.object(fieldNum(EUSalesList, TaxRep349AmountMstPrev)).mandatory(false);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarter)).mandatory(false);
        }

        //update service correction controls
        if (euSalesList.CorrectedServices)
        {
            this.updateCorrectionGroupControls(correctionServices, EUSalesList_CorrectedServices.id(), true);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionYearServices)).mandatory(true);
            euSalesList_ds.object(fieldNum(EUSalesList, TaxRep349AmountMstPrevServices)).mandatory(true);

            if (euSalesList.CorrectionPeriodServices == EUSalesListCorrectionPeriod_ES::Month
                || euSalesList.CorrectionPeriodServices == EUSalesListCorrectionPeriod_ES::Quarter)
            {
                correctionServices_CorrectionMonthOrQuarterServices.enabled(true);
                euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarterServices)).mandatory(true);
            }
            else
            {
                correctionServices_CorrectionMonthOrQuarterServices.enabled(false);
                euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarterServices)).mandatory(false);
            }
        }
        else
        {
            this.updateCorrectionGroupControls(correctionServices, EUSalesList_CorrectedServices.id(), false);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionYearServices)).mandatory(false);
            euSalesList_ds.object(fieldNum(EUSalesList, TaxRep349AmountMstPrevServices)).mandatory(false);
            euSalesList_ds.object(fieldNum(EUSalesList, CorrectionMonthOrQuarterServices)).mandatory(false);
        }
    }
}

private void updateCorrectionGroupControls(FormGroupControl _groupControl, int _skipControlId, boolean _enabled)
{
    int i;
    for (i = 1; i <= _groupControl.controlCount(); i++)
    {
        if (_groupControl.controlNum(i).id() != _skipControlId)
        {
            _groupControl.controlNum(i).enabled(_enabled);
        }
    }
}

void updateDeliveryCodeState()
{
    if (EUSalesList.Direction == ModuleSalesPurch::Purch
    || EUSalesList.AmountMST == 0
    || EUSalesList.Listcode == Listcode::TriangularEUTrade
    || EUSalesList.Listcode == Listcode::TriangularProductionOnToll)
    {
        Eusaleslist_ds.object(fieldNum(EUSalesList, DeliveryCode_ES)).enabled(false);
    }
    else
    {
        Eusaleslist_ds.object(fieldNum(EUSalesList, DeliveryCode_ES)).enabled(true);
    }
}

private boolean supportsCorrection()
{
    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES]);
}

private str getListStatusRangeValue()
{
    str res;

    switch (comboBoxSelect.selection())
    {
        case EUSalesListStatusFilter::Included :
            res = queryValue(ListStatus::Include);
            break;
        case EUSalesListStatusFilter::Reported :
            res = queryValue(ListStatus::Reported);
            break;
        case EUSalesListStatusFilter::Closed :
            res = queryValue(ListStatus::Closed);
            break;
        default:
            res = SysQuery::valueUnlimited();
    }

    return res;
}

public void executeQuery()
{
    selectListStatusRange.value(element.getListStatusRangeValue());

    super();
}

public void init()
{
    super();

    selectListStatusRange = this.query().dataSourceNo(1).addRange(fieldNum(EUSalesList,Status));
    selectListStatusRange.status(RangeStatus::Locked);
}

public void create(boolean _append = false)
{
    if (!num)
    {
        numberSeq       = NumberSeq::newGetNum(IntrastatParameters::numRefListDispatchId(), true);
        num             = numberSeq.num();
        seqNumCounter   = EUSalesList::maxAllSeqNum();
    }
    super(_append);
}

public void initValue()
{
    seqNumCounter++;
    euSalesList.DispatchId = num;
    euSalesList.Listcode   = Listcode::EUTrade;
    euSalesList.SeqNum = seqNumCounter;

    super();
}

int active()
{
    int ret;

    ret = super();

    if (euSalesList.Log)
        buttonErrorlog.enabled(true);
    else
        buttonErrorlog.enabled(false);

    if (euSalesList.Status == ListStatus::Closed)
        euSalesList_ds.allowEdit(false);
    else
        euSalesList_ds.allowEdit(true);

    element.showHideTotals();

    element.updateCorrectionControls();

    element.updateDeliveryCodeState();

    return ret;
}

display ImageRes checkIfError(EUSalesList _euSalesList)
{
    ImageRes iconImage;
    str helpText;

    if (!_euSalesList.RecId)
    {
        helpText = "@SYS345343";
        iconImage = 0;
    }
    else if (_euSalesList.Log)
    {
        helpText = "@SYS345344";
        iconImage = #ImageError;
    }
    else
    {
        helpText = "@SYS345343";
        iconImage = #ImageOK;
    }

    ok.helpText(helpText);

    return iconImage;
}

