void clicked()
{
    super();
    if (euSalesList.Log)
        InfoLogExtension::showMessagesFromContainer(Info::infoStr2Con(euSalesList.Log));
    else
        info("@SYS19186");
}

boolean modified()
{
    boolean ret;

    ret = super();

    euSalesList_ds.executeQuery();

    return ret;
}

void jumpRef()
{
    if (TaxVATNumTable::taxVATNumTableEnabled())
    {
        TaxVATNumTable::jumpRefVATNum(euSalesList, element);
    }
    else
    {
        super();
    }
}

void lookup()
{
    if (TaxVATNumTable::taxVATNumTableEnabled())
    {
        TaxVATNumTable::lookupVATNum(euSalesList_VATNum,euSalesList);
    }
    else
    {
        super();
    }
}
