/// <summary>
/// Performs the necessary actions when the add-attribute form is closing.
/// </summary>
public void addAttributesFormClosing()
{
    ecoResAttributeGroupAttribute_ds.research();
    element.syncCategoryAttributeLookup();
}

public void defaultValueSetInvisible()
{
    defaultValueStringEdit.visible(false);

    // Cannot hide multiline rich edit textbox here. Hide in defaultValueSetVisible().
    // Otherwise the multiline textbox will disappear when it has a few lines of HTML content.
    defaultValueRichEditButton.enabled(false);

    attributeDefaultValueTranslateButton.enabled(false);
    defaultValueIntEdit.visible(false);
    defaultValueUtcDateTimeEdit.visible(false);
    defaultValueRealEdit.visible(false);
    defaultCurrencyValueRealEdit.visible(false);
    defaultValueBooleanEdit.visible(false);
    enumerationComboBox.visible(false);
    floatUnitOfMeasure.visible(false);
    intUnitOfMeasure.visible(false);
    currencyCode.visible(false);
    integerDecimalGroup.visible(false);
    currencyGroup.visible(false);
}

public void defaultValueSetVisible()
{
    RetailRichMediaType richMediaType;
    str richMediaEditFormMenuItemName;

    if (ecoResValue_Default)
    {
        switch (ecoResValue_Default.getInstanceRelationType())
        {
            case tableStr(EcoResBooleanValue):
                defaultValueBooleanEdit.visible(true);
                break;
            case tableStr(EcoResDateTimeValue):
                defaultValueUtcDateTimeEdit.visible(true);
                break;
            case tableStr(EcoResFloatValue):
                defaultValueRealEdit.visible(true);
                integerDecimalGroup.visible(true);
                if (EcoResAttributeTypeUnitOfMeasure::findByAttributeType(ecoResAttributeType).RecId)
                {
                    floatUnitOfMeasure.visible(true);
                }
                break;
            case tableStr(EcoResCurrencyValue):
                defaultCurrencyValueRealEdit.visible(true);
                currencyCode.visible(true);
                currencyGroup.visible(true);
                break;
            case tableStr(EcoResIntValue):
                defaultValueIntEdit.visible(true);
                integerDecimalGroup.visible(true);
                if (EcoResAttributeTypeUnitOfMeasure::findByAttributeType(ecoResAttributeType).RecId)
                {
                    intUnitOfMeasure.visible(true);
                }
                break;
            case tableStr(EcoResTextValue):
                if (ecoResAttributeType.IsEnumeration == NoYes::Yes)
                {
                    enumerationComboBox.visible(true);
                }
                else
                {
                    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
                    {
                        richMediaType = RetailAttributeHelper::getRichMediaType(ecoResAttributeType.RecId);
                        richMediaEditFormMenuItemName = RetailRichMediaHelper::getRichMediaEditFormMenuItemName(richMediaType);
                    }

                    if (richMediaEditFormMenuItemName)
                    {
                        defaultValueRichEdit.multiLine(richMediaType == RetailRichMediaType::Html);
                        defaultValueRichEdit.visible(true);

                        defaultValueRichEditButton.menuItemName(richMediaEditFormMenuItemName);
                        defaultValueRichEditButton.enabled(true);
                    }
                    else
                    {
                        defaultValueStringEdit.visible(true);
                    }

                    attributeDefaultValueTranslateButton.enabled(true);
                }
                break;
            default:
            // Unsupported
        }
    }

    if (!richMediaEditFormMenuItemName)
    {
        defaultValueRichEdit.visible(false);
    }
}

public void domainChanged()
{
    EcoResTextValue enumerationValue;
    EcoResEnumerationAttributeTypeValue enumerationAttributeTypeValue;

    if (ecoResAttributeGroupAttribute.RecId && EcoResAttribute.RecId && !ecoResAttributeGroupAttribute.DefaultValue &&
        !AttributeDataTypeDiscriminator::checkAttributeDataTypeReference(EcoResAttributeType.DataType))
    {
        RefRecId defaultValueRecId = EcoResValue::createDefaultValue(EcoResAttribute.RecId);

        if (!defaultValueRecId)
        {
            checkFailed(strFmt("@SCM:EcoResDefaultAttributeValueFailOnCreate", EcoResAttribute::find(ecoResAttributeGroupAttribute.Attribute).Name, "@SYS300535"));
        }
        else
        {
            ttsbegin;

            ecoResAttributeGroupAttribute.reread();
            ecoResAttributeGroupAttribute.selectForUpdate(true);
            ecoResAttributeGroupAttribute.DefaultValue = defaultValueRecId;
            ecoResAttributeGroupAttribute.update();

            ttscommit;

            EcoResAttributeGroupAttribute localEcoResAttributeGroupAttribute;
            localEcoResAttributeGroupAttribute.data(ecoResAttributeGroupAttribute);

            ecoResAttributeGroupAttribute_ds.reread();
            ecoResAttributeGroupAttribute_ds.research(false);
            EcoResAttributeGroupAttribute_ds.positionToRecord(localEcoResAttributeGroupAttribute);
        }
    }

    if (AttributeDataTypeDiscriminator::checkAttributeDataTypeText(ecoResAttributeType.DataType) && 
        ecoResAttributeType.IsEnumeration)
    {
        // Populate enumeration combo box
        enumerationComboBox.clear();
        while select TextValue from enumerationValue
            exists join enumerationAttributeTypeValue
            where enumerationAttributeTypeValue.Value == enumerationValue.RecId
               && enumerationAttributeTypeValue.AttributeType == ecoResAttributeType.RecId
        {
            enumerationComboBox.add(enumerationValue.TextValue);
        }
        enumerationComboBox.selectText(ecoResValue_Default_EcoResTextValue.TextValue);
    }

    EcoResAttributeType attributeType;

    if (ecoResAttribute.AttributeType && !ecoResAttributeType)
    {
        attributeType = EcoResAttributeType::find(ecoResAttribute.AttributeType);
    }
    else
    {
        attributeType = ecoResAttributeType;
    }

    attributeDefaultValueHelper = new EcoResAttributeDefaultValueHelper(attributeType);

    // Set visibility of controls
    element.defaultValueSetInvisible();
    element.defaultValueSetVisible();
}

public void enumerationChanged()
{
    // Update default text value
    ecoResValue_Default_EcoResTextValue.TextValue = enumerationComboBox.getText(enumerationComboBox.selection());
}

public void init()
{
    super();
