        EcoResAttributeGroupAttribute_ds.queryBuildDataSource().addSortField(fieldNum(EcoResAttributeGroupAttribute, RetailDisplayOrder));
    }
}

public void syncCategoryAttributeLookup()
{
    EcoResCategoryAttributeGroup ecoResCategoryAttributeGroup;
    EcoResCategory ecoResCategory;

    if (EcoResProductAttributeGroupAttributeCategoryLookupPerfFlight::instance().isEnabled())
    {
        EcoResCategory rootCategory;

        while select rootCategory
                where rootCategory.Level == 1
                exists join ecoResCategory
                    where ecoResCategory.CategoryHierarchy == rootCategory.CategoryHierarchy
                exists join ecoResCategoryAttributeGroup
                    where ecoResCategoryAttributeGroup.AttributeGroup == ecoResAttributeGroup.RecId
                       && ecoResCategoryAttributeGroup.Category == ecoResCategory.RecId
                       && ecoResCategoryAttributeGroup.Modifier == EcoResCategoryAttributeModifier::Product
        {
            rootCategory.ForceFullLookupSync = NoYes::Yes;
            EcoResCategoryAttributeLookup::synchronizeWithCategoryAttribute(rootCategory);
        }
    }
    else
    {
        while select ecoResCategory
            exists join ecoResCategoryAttributeGroup
            where ecoResCategoryAttributeGroup.AttributeGroup == ecoResAttributeGroup.RecId
               && ecoResCategoryAttributeGroup.Category == ecoResCategory.RecId
               && ecoResCategoryAttributeGroup.Modifier == EcoResCategoryAttributeModifier::Product
        {
            EcoResCategoryAttributeLookup::synchronizeWithCategoryAttribute(ecoResCategory);
        }
    }
}

