/// <summary>
/// Checks whether the current attribute is of type Image attribute.
/// </summary>
/// <returns>true if the Attribute is of Image type; otherwise, false.</returns>
private boolean isRetailImageAttribute()
{
    boolean isImageAttribute = false;
    RetailMediaMasterEntity selectedEntity = this.getRetailMediaEntityType();

    // Check if the selected attribute group is of type Catalog, Customer, Category, Worker or Product.
    if (selectedEntity == RetailMediaMasterEntity::Catalog || selectedEntity == RetailMediaMasterEntity::Category
        || selectedEntity == RetailMediaMasterEntity::Customer || selectedEntity == RetailMediaMasterEntity::Worker
        || selectedEntity == RetailMediaMasterEntity::Product)
    {
        EcoResAttributeType attributeType = EcoResAttributeType::findByAttribute(ecoResAttributeGroupAttribute.Attribute);

        if (attributeType)
        {
            isImageAttribute = RetailAttributeHelper::isAttributeTypeImages(attributeType.RecId);
        }
    }

    return isImageAttribute;
}

/// <summary>
/// Returns the <c>RetailMediaMasterEntity</c> based on the selected Retail attribute.
/// </summary>
/// <returns>The <c>RetailMediaMasterEntity</c> value.</returns>
private RetailMediaMasterEntity getRetailMediaEntityType()
{
    RetailSharedParameters retailSharedParameters = RetailSharedParameters::find();
    RetailMediaMasterEntity mediaEntity;

    if (retailSharedParameters.CatalogDefaultAttributeGroup == ecoResAttributeGroupAttribute.AttributeGroup)
    {
        mediaEntity = RetailMediaMasterEntity::Catalog;
    }
    else if (retailSharedParameters.CategoryDefaultAttributeGroup == ecoResAttributeGroupAttribute.AttributeGroup)
    {
        mediaEntity = RetailMediaMasterEntity::Category;
    }
    else if (retailSharedParameters.CustomerDefaultAttributeGroup == ecoResAttributeGroupAttribute.AttributeGroup)
    {
        mediaEntity = RetailMediaMasterEntity::Customer;
    }
    else if (retailSharedParameters.WorkerDefaultAttributeGroup == ecoResAttributeGroupAttribute.AttributeGroup)
    {
        mediaEntity = RetailMediaMasterEntity::Worker;
    }
    else if (retailSharedParameters.ProductDefaultAttributeGroup == ecoResAttributeGroupAttribute.AttributeGroup)
    {
        mediaEntity = RetailMediaMasterEntity::Product;
    }

    return mediaEntity;
}

/// <summary>
/// Open the Retail image template form so that user can edit the image template.
/// </summary>
/// <param name = "selectedEntity">The <c>RetailMediaMasterEntity</c> value.</param>
private void openRetailImageTemplateForm(RetailMediaMasterEntity selectedEntity)
{
    Args                args;
    FormRun             formRun;

    //Open the RetailMediaAssociation form
    args = new Args();
    args.name(formStr(RetailMediaAssociation));
    args.parmEnum(selectedEntity);
    formRun = classfactory.formRunClass(args);

    if (formRun)
    {
        formRun.run();
        formRun.wait();
    }
}

