public boolean validateDelete()
{
    EcoResCategoryAttributeGroup  ecoResCategoryAttributeGroup;
    EcoResCategory ecoResCategory;
    EcoResCategoryHierarchy ecoResCategoryHierarchy;

    boolean ret;

    ret = super();

    if (!ret)
    {
        select firstOnly RecId from ecoResCategoryAttributeGroup
            where ecoResCategoryAttributeGroup.AttributeGroup == ecoResAttributeGroup.RecId;

        if (ecoResCategoryAttributeGroup.RecId)
        {
            while select RecId, Name, CategoryHierarchy from ecoResCategory
                join Name from ecoResCategoryHierarchy
                    where ecoResCategoryHierarchy.RecId == ecoResCategory.CategoryHierarchy
                exists join ecoResCategoryAttributeGroup
                    where ecoResCategoryAttributeGroup.Category == ecoResCategory.RecId
                    && ecoResCategoryAttributeGroup.AttributeGroup == ecoResAttributeGroup.RecId
            {
                info(strFmt('@SCM:AttrGrpAssocWithCategory', ecoResCategory.Name, ecoResCategoryHierarchy.Name));
            }
        }
    }

    return ret;
}

