public void init()
{
    super();
    this.filterAttributeGroupsOnCustomer();
}

/// <summary>
/// Filter customer attribute group to be not shown if the attribute group is
/// being used by RetailParameters table in different legal entities.
/// </summary>
private void filterAttributeGroupsOnCustomer()
{
    QueryBuildDataSource eraQbds;
    RetailParameters currentRp, rp;
    TextBuffer queryStrBuffer;

    // Only applies on Retail configuration.
    if (!isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        return;
    }

    currentRp = RetailParameters::find();
    eraQbds = this.query().dataSourceTable(this.table());

    // Find record identifier of Customer attribute group
    // that are being used by Retail Parameters on other legal entities.
    // Use the result as 'not-in' on the query.
    while select crosscompany CustomerAttributeGroup from rp
        where rp.CustomerAttributeGroup
        && rp.CustomerAttributeGroup != currentRp.CustomerAttributeGroup
    {
        if (queryStrBuffer == null)
        {
            // First match filter, instantiate new TextBuffer.
            queryStrBuffer = new TextBuffer();
            queryStrBuffer.appendText(strFmt('(%1 != %2)', fieldStr(EcoResAttributeGroup, RecId), rp.CustomerAttributeGroup));
        }
        else
        {
            // TextBuffer already instantiated and already has some filters, add clause '&&'
            queryStrBuffer.appendText(strFmt('&& (%1 != %2)', fieldStr(EcoResAttributeGroup, RecId), rp.CustomerAttributeGroup));
        }

    }

    if (queryStrBuffer != null)
    {
        eraQbds.addRange(fieldNum(EcoResAttributeGroup, RecId)).value(queryStrBuffer.getText());
    }
}

