public void clicked()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        //Open the Retail image template form when the selected attribute is Image for Catalog, Category, Customer, Worker and Product.
        if (element.isRetailImageAttribute())
        {
            element.openRetailImageTemplateForm(element.getRetailMediaEntityType());
            return;
        }
    }

    super();
}

