    return ret;
}

public boolean validate()
{
    boolean isValid;
    EcoResDateTimeValue ecoResDateTimeValue;

    isValid = super();
    ecoResDateTimeValue.DateTimeValue = DateTimeUtil::removeTimeZoneOffset(this.dateTimeValue(), DateTimeUtil::getUserPreferredTimeZone());

    if (ecoResBoundedAttributeTypeValue)
    {
        isValid = element.validateDefaultValue(ecoResDateTimeValue);

        if (!isValid)
        {
            warning(strFmt("@SYS310456", ecoResDateTimeValue.value(), 
                ecoResBoundedAttributeTypeValue::findByAttributeType(EcoResAttributeType::find(ecoResAttribute.AttributeType)).toString(), ecoResAttributeType.Name));
        }
    }
    else
    {
        if (ecoResBoundedAttributeTypeValue && !ecoResBoundedAttributeTypeValue.isValid(ecoResDateTimeValue))
        {
            isValid = false;
            warning(strFmt("@SYS310456", this.dateTimeValue(), ecoResBoundedAttributeTypeValue.toString(), ecoResAttributeType.Name));
        }
    }

    return isValid;
}

public boolean validate()
{
    boolean isValid;
    EcoResIntValue ecoResIntValue;
    EcoResAttributeTypeUnitOfMeasure ecoResAttributeTypeUnitOfMeasure = EcoResAttributeTypeUnitOfMeasure::findByAttributeType(ecoResAttributeType);

    isValid = super();            
    ecoResIntValue.IntValue = this.value();

    if (ecoResAttributeTypeUnitOfMeasure)
    {
        ecoResIntValue.IntUnitOfMeasure = UnitOfMeasure::find(ecoResAttributeTypeUnitOfMeasure.DefaultUnitOfMeasure).RecId;
    }

    if (ecoResBoundedAttributeTypeValue)
    {
        isValid = element.validateDefaultValue(ecoResIntValue);

        if (!isValid)
        {
            warning(strFmt("@SYS310456", ecoResIntValue.value(), 
                ecoResBoundedAttributeTypeValue::findByAttributeType(EcoResAttributeType::find(ecoResAttribute.AttributeType)).toString(),
                ecoResAttributeType.Name));
        }
    }
    else
    {                
        if (ecoResBoundedAttributeTypeValue && !ecoResBoundedAttributeTypeValue.isValid(ecoResIntValue))
        {
            isValid = false;
            warning(strFmt("@SYS310456", this.value(), ecoResBoundedAttributeTypeValue.toString(), ecoResAttributeType.Name));
        }
    }

    return isValid;
}

