public void close()
{
    super();

    // synchronize in case categories were added or removed from retail categories
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        RetailCategoryContainmentLookup::synchronizeCategoriesLookup();
        FormRun callerForm = element.args().caller();
        if (callerForm.name() == formStr(RetailCategory))
        {
            callerForm.refreshCategories();
        }
    }

    element.refreshEcoResCategoryHierarchyListPage();
    element.refreshProcCategoryHierarchyTree();
}

	private void refreshProcCategoryHierarchyTree()
    {
        FormRun callerForm = element.args().caller();
        ICategoryHierarchyTree categoryHierarchyForm = callerForm as ICategoryHierarchyTree;
        if (categoryHierarchyForm && callerForm.name() == formStr(ProcCategoryHierarchyManagement))
        {
            categoryHierarchyForm.rebuildCategoryHierarchyTree();
        }
	}

private void refreshEcoResCategoryHierarchyListPage()
{
    FormRun parentForm = element.args().caller();
    if (parentForm && parentForm.name() == formStr(EcoResCategoryHierarchyCreate))
    {
        FormRun originalParent = parentForm.args().caller();
        if (originalParent)
        {
            List dataSourceList = originalParent.rootFormDataSources();

            if (dataSourceList && dataSourceList.elements() > 0)
            {
                ListEnumerator dsListEnumerator = dataSourceList.getEnumerator();

                while (dsListEnumerator.moveNext())
                {
                    FormDataSource formDS = dsListEnumerator.current();
                    if (formDS.table() == tableNum(EcoResCategoryHierarchy))
                    {
                        formDS.research(true);
                        break;
                    }
                }
            }
        }
    }
}

void dropHilite(FormTreeControl _formTreeControl, int _idx)
{
    FormTreeItem    formTreeItem;

    if (lastHilitedItemIdx)
    {
        formTreeItem        = _formTreeControl.getItem(lastHilitedItemIdx);
        lastHilitedItemIdx  = 0;

        formTreeItem.stateDropHilited(false);
        _formTreeControl.setItem(formTreeItem);
    }

    if (_idx)
    {
        lastHilitedItemIdx = _idx;
        formTreeItem       = _formTreeControl.getItem(lastHilitedItemIdx);

        formTreeItem.stateDropHilited(true);
        _formTreeControl.setItem(formTreeItem);
    }
}

