/// <summary>
/// Initializes the form. Initializes various form members variables.
/// </summary>
void init()
{
    EcoResCategoryHierarchyId       ecoResCategoryHierarchyReferenceRecId;
    FormRun                         callerFormRun = element.args().caller();
    Common                          record = element.args().record();
    PurchRFQCaseLine                rfqCaseLine;
    RecId                           rfqCaseLineCategoryRecId;

    #ISOCountryRegionCodes

    QueryBuildDataSource attributeFilter_ds;

    super();

    this.setupFallbackProductTranslation();

    if (!isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        EcoResCategoryAttributeGroup_Category_ds.query().dataSourceTable(tableNum(EcoResCategoryAttributeGroup)).enabled(false);
        EcoResAttributeGroup_Category_ds.query().dataSourceTable(tableNum(EcoResAttributeGroup)).enabled(false);
        EcoResCategory_InheritedCategory_ds.query().dataSourceTable(tableNum(EcoResCategory)).enabled(false);
        EcoResAttributeGroupTranslation_Category_ds.query().dataSourceTable(tableNum(EcoResAttributeGroupTranslation)).enabled(false);
    }

    // The below if condition will handle the category reference form's right click "view details" scenario(s).
    // All possible scenarios and the desired behaviors are given below:
    // ----------------------------------------------------------------------------------------------------------------------------------------------------------------
    // View details called from..           Contains data?                            Desired Behavior
    // ----------------------------------------------------------------------------------------------------------------------------------------------------------------
    // Category hierarchy cell              Contains data                             Launch the generic category hierarchy details form, with the root node highlighted.
    //                                      Does not contain data (i.e., blank)       Launch the generic category hierarchy list page form.
    // Category cell                        Contains data                             Launch the generic category hierarchy details form, with the selected node, from the cell highlighted.
    //                                      Does not contain data (i.e., blank)       Launch the generic category hierarchy list  page form.
    if (element.args().lookupTable() == tablenum(EcoResCategory) || element.args().lookupTable() == tableNum(EcoResCategoryHierarchy))
    {
        // if the refernce form does not have category/hierarchy value then navigate to the Category hierarchy list page
        if (element.args().lookupValue() == "0")
        {
            new MenuFunction(menuitemDisplayStr(EcoResCategoryHierarchyListPage), MenuItemType::Display).run();
            element.close(); // need to close the current details page from rendering
            return;
        }

        if (element.args().lookupTable() == tablenum(EcoResCategory))// if view details called from category cell
        {
            erCategoryreferenceRecId = str2int64(element.args().lookupValue()); // reading the category reference value.

            if ( EcoResCategory::checkExist(erCategoryReferenceRecId))
            {
                element.args().record(EcoResCategoryHierarchy::find(EcoResCategory::find(erCategoryReferenceRecId).CategoryHierarchy));
            }
        }
        else if (element.args().lookupTable() == tablenum(EcoResCategoryHierarchy)) // if view details called from category hierarchy cell
        {
            ecoResCategoryHierarchyReferenceRecId = str2int64(element.args().lookupValue()); // reading the category hierarchy reference value.
            if (EcoResCategoryHierarchy::checkExist(ecoResCategoryHierarchyReferenceRecId))
            {
                element.args().record(EcoResCategoryHierarchy::find(ecoResCategoryHierarchyReferenceRecId));
            }
        }
    }
    else if (element.args().dataset() == tableNum(PurchRFQCaseLine))
    {
        // this form would be opened when, in form PurchRFQCaseTable, Line grid, ProcurementCategory column,
        // (bound to PurchRFQCaseLine.CategoryId, which is FK on EcoResCategory,
        //      which has FormRef=#EcoResCategoryHierarchyDetails,
        //      which is the MenuItem tied to this form
        // )
        // user does right-click, ViewDetails

        // we set 2 pieces of info accordingly, which will be used later:
        // - the hierarchy
        // - the EcoResCategory
        element.args().record(CatProcureCatalogTable::findProcurementHierarchy());
        rfqCaseLine = record;
        if (rfqCaseLine)
        {
            rfqCaseLineCategoryRecId = rfqCaseLine.CategoryId;
            if (rfqCaseLineCategoryRecId)
            {
                // set the record wwhich will later be made the selected in in the true
                erCategorySelectedNode = EcoResCategory::find(rfqCaseLineCategoryRecId);
            }
        }
    }
    else if (element.args().dataset() == tableNum(EcoResProductCategoryAttributeStaging))
    {
        EcoResProductCategoryAttributeStaging ecoResProductCategoryAttributeStaging = element.args().record();
        element.args().record(EcoResCategoryHierarchy::findByName(ecoResProductCategoryAttributeStaging.ProductCategoryHierarchyName));
    }
    else if (element.args().dataset() == tableNum(EcoResProductCategoryStaging))
    {
        EcoResProductCategoryStaging ecoResProductCategoryStaging = element.args().record();
        element.args().record(EcoResCategoryHierarchy::findByName(ecoResProductCategoryStaging.ProductCategoryHierarchyName));
    }
    else if (!callerFormRun ||
             !element.args().dataset() ||
              element.args().dataset()  != tableNum(EcoResCategoryHierarchy))
    {
        throw error(strFmt("@SYS22678", element.name()));
    }

    isFormOpenedByMenuItemInNewMode = (element.args().openMode() == OpenMode::New);

    currentSelectedCategoryHierarchy = element.args().record();

    callerFormDatasource = callerFormRun.dataSource();

    if (callerFormRun.name() == formStr(ProcCategoryHierarchyManagement)
        && callerFormDatasource.cursor() is EcoResCategory)
    { 
        erCategorySelectedNode = callerFormDataSource.cursor() as EcoResCategory;
    }

    ecoResCategory_DefaultThreshold_PSN.visible(PublicSectorUtils::isFrenchRegulatoryEnabled());

    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        this.initializeDisplayOrderControls();
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        attributeFilter_ds = ecoResAttribute_Category_ds.query().dataSourceTable(tableNum(EcoResAttribute))
                                .addDataSource(tableNum(EcoResAttributeType), 'VisibleAttributeFilter');

        attributeFilter_ds.joinMode(JoinMode::ExistsJoin);
        attributeFilter_ds.addLink(fieldNum(EcoResAttribute, AttributeType), fieldNum(EcoResAttributeType, recId));

        attributeFilter_ds.addRange(fieldNum(EcoResAttributeType, IsHidden)).value(queryValue(NoYes::No));
    }

    GST_IN.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]));
    TaxRateType.visible(TaxIntegrationUtils::isTaxInformationEnabled());
}

void initializeHelper()
{
    QueryBuildRange                 range;
    EcoResCategory                  root;
    EcoResCategoryLookupParameters  lookupParameters = new EcoResCategoryLookupParameters(false, false, false);

    range = ecoResCategoryHierarchy_ds.query().dataSourceTable(tableNum(EcoResCategoryHierarchy)).addRange(fieldNum(EcoResCategoryHierarchy, RecId));
    range.value( queryValue(currentSelectedCategoryHierarchy.RecId ) );
    range.status(RangeStatus::Hidden);
    ecoResCategoryHierarchy_ds.executeQuery();

    range = ecoResCategory_ds.query().dataSourceTable(tableNum(EcoResCategory)).addRange(fieldNum(EcoResCategory, CategoryHierarchy));
    range.value( queryValue(currentSelectedCategoryHierarchy.RecId ) );
    range.status(RangeStatus::Hidden);

    ecoResCategory_ds.executeQuery();

    root = EcoResCategory::getRoot(currentSelectedCategoryHierarchy.RecId);
    if (!root)
    {
        element.setFormElementState();
        return;
    }

    hierarchyTree = new EcoResCategoryTreeDatasource(  ecoResCategory_ds,
                                                       ctrlFormTree,
                                                       fieldNum(EcoResCategory, RecId),
                                                       fieldNum(EcoResCategory, ParentCategory),
                                                       fieldNum(EcoResCategory, Name),
                                                       false /* No ID in description */,
                                                       true,
                                                       fieldNum(EcoResCategory, Code),
                                                       currentSelectedCategoryHierarchy,
                                                       ecoResCategoryHierarchy_ds,
                                                       lookupParameters,
                                                       fieldNum(EcoResCategory, NestedSetLeft) /* for tree sort order */
                                                       );

    hierarchyTree.initRoot(root.Name, root.RecId, hierarchyTree.image() );
}

