/// <summary>
/// this method will be executed only if current form opened from ProcCategoryHierarchyManagement's category page drop down "Goto main table form" option.
/// performs the following
/// 1. Retains the category tree state as it is on the caller form (ProcCategoryHierarchyManagement)
/// 2. Expand the CategoryPage fast tab and collapse rest of the fast tabs
/// </summary>
private void processGotoMainforProcCatPage()
{
    Map recordsMap              =  element.args().parmObject();
    catPageSelectedRecord       =  recordsMap.lookup(tableNum(ProcCategoryPage));
    erCategorySelectedNode      =  recordsMap.lookup(tableNum(EcoResCategory));
    isFromProcCatPageJumpRef    =  true;

    element.showSelectedNodeByExpand(erCategorySelectedNode.RecId);

    hierarchyDetails.collapse();
    ecoResCategoryAttributeCategoryTabPage.collapse();
    categoryCommodityCodeTabPage.collapse();
    projectCategoriesTabPage.collapse();
    categoryPagesTabPage.expand();
}

/// <summary>
/// Rebuilds the query for the EcoResCategoryAttributeGroup datasource,
/// then refreshes the grid listing all attribute groups of the selected category.
/// </summary>
public void refreshCategoryAttributeGroupListGrid()
{
    QueryBuildDataSource qbdsCategory           = ecoResCategory_InheritedCategory_ds.query().dataSourceTable(tableNum(EcoResCategory));
    QueryBuildDataSource qbdsCategoryAttributeGroups = ecoResCategoryAttributeGroup_Category_ds.query().dataSourceTable(tableNum(EcoResCategoryAttributeGroup));

    //set range for category attribute modifier
    qbdsCategoryAttributeGroups.clearRanges();
    qbdsCategoryAttributeGroups.addRange(fieldNum(EcoResCategoryAttributeGroup, Modifier)).value(queryValue(EcoResCategoryAttributeModifier::Category));

    //set range for category attribute modifier
    qbdsCategory.clearRanges();
    if (NoYes::No == ecoResCategory.IsCategoryAttributesInherited)
    {
        qbdsCategory.addRange(fieldNum(EcoResCategory,RecId)).value(queryValue(ecoResCategory.RecId));
    }
    else
    {
        EcoResCategory::setCategoryRangeBasedOnInheritance(ecoResCategory_InheritedCategory_ds, ecoResCategory, fieldNum(EcoResCategory, IsCategoryAttributesInherited) );
    }

    // finally, refreshes UI
    ecoResCategoryAttributeGroup_Category_ds.executeQuery();
}

/// <summary>
/// Rebuilds the query for the EcoResCategory_ForCategoryAttributes datasource,
/// then refreshes the grid listing all attributes of the selected category.
/// </summary>
public void refreshCategoryAttributeListGrid()
{
    QueryBuildDataSource qbdsCategory           = ecoResCategory_Category_ds.query().dataSourceTable(tableNum(EcoResCategory));
    QueryBuildDataSource qbdsCategoryAttributes = ecoResCategoryAttribute_Category_ds.query().dataSourceTable(tableNum(EcoResCategoryAttribute));

    //set range for category attribute modifier
    qbdsCategoryAttributes.clearRanges();
    qbdsCategoryAttributes.addRange(fieldNum(EcoResCategoryAttribute, Modifier)).value(queryValue(EcoResCategoryAttributeModifier::Category));

    //set range for category attribute modifier
    qbdsCategory.clearRanges();
    if (NoYes::No == ecoResCategory.IsCategoryAttributesInherited)
    {
        qbdsCategory.addRange(fieldNum(EcoResCategory,RecId)).value(queryValue(ecoResCategory.RecId));
    }
    else
    {
        EcoResCategory::setCategoryRangeBasedOnInheritance(ecoResCategory_Category_ds, ecoResCategory, fieldNum(EcoResCategory, IsCategoryAttributesInherited) );
    }

    // finally, refreshes UI
    ecoResCategoryAttribute_Category_ds.executeQuery();
}

