/// <summary>
/// Looks through the selected records (attributes or attribute groups) and deletes only those ones that are not inherited from the parent category.
/// </summary>
/// <param name='_formDS'>
/// A form data source, where record deletion is performed.
/// </param>
public void removeAttributeItems(FormDataSource _formDS)
{
    // assign current table buffer to map variable
    EcoResCategoryAttributeItemMap  selectedMap = _formDS.cursor();
    // create a new table buffer
    EcoResCategoryAttributeItemMap  tempMap = new SysDictTable(_formDS.table()).makeRecord();
    int                             recordsSelectedToDelete = 0;
    int                             deletedRecords = 0;
    str                             infoMessage = '';

    ttsbegin;
    // Are there any marked records?
    for (selectedMap = getFirstSelection(_formDS);
    selectedMap;
    selectedMap = _formDS.getNext())
    {
        // walk through the selected records...
        recordsSelectedToDelete++;
        tempMap = tempMap.EcoResCategoryAttributeItemMap::find(selectedMap.RecId, true);

        if ((tempMap.Category == ecoResCategory.RecId) &&
                tempMap.Modifier == EcoResCategoryAttributeModifier::Category &&
                    tempMap.validateDelete())
        {
            // ... and only delete those ones that are not inherited from parent
            tempMap.delete();
            deletedRecords++;
        }
    }
    ttscommit;

    if (deletedRecords > 0)
    {
        _formDS.executeQuery();
    }

    // some are inherited hence cannot be deleted
    if (recordsSelectedToDelete > deletedRecords)
    {
        // show infolog message depending on the caller
        switch (_formDS.table())
        {
            case tableNum(EcoResCategoryAttribute):
                infoMessage = "@SYS135673";
                break;
            case tableNum(EcoResCategoryAttributeGroup):
                infoMessage = "@RET261182";
                break;
            default:
                break;
        }

        info(strFmt(infoMessage, deletedRecords, (recordsSelectedToDelete - deletedRecords)));
    }
}

void rename()
{
    hierarchyDetails.activatePage();
    hierarchyDetails.setFocus();
    ecoResCategory_Name.setFocus();
}

public void run()
{
    super();

    if (currentSelectedCategoryHierarchy && currentSelectedCategoryHierarchy.RecId)
    {
        element.initializeHelper();
        element.rename();
    }

    if (element.args().parm() == 'fromProcCatPageJumpRef')
    {
        element.processGotoMainforProcCatPage();
    }
    else if (erCategorySelectedNode)
    {
        element.showSelectedNodeByExpand(erCategorySelectedNode.RecId);
    }

    // if the form opened from right click "view details" option from referenced form(s).
    if (element.args().lookupTable() == tableNum(EcoResCategory)  && element.args().lookupValue() != "0")
    {
        // expand the tree and select the required node
        element.showSelectedNodeByExpand(erCategoryReferenceRecId);
    }
}

void setButtonAccess()
{
    projectCategoriesTabPage.visible(ecoResCategoryHierarchy.allowEditProjectGlobalCategory());

    if (ecoResCategoryHierarchy.hasRetailRole())
    {
        if (!ecoResCategoryHierarchy.hasNonRetailRole())
        {
            categoryCommodityCodeTabPage.enabled(false);
            categoryCommodityCodeTabPage.visible(false);
            categoryPagesTabPage.enabled(false);
            categoryPagesTabPage.visible(false);
            ecoResCategoryAttributeCategoryTabPage.enabled(false);
            ecoResCategoryAttributeCategoryTabPage.visible(false);
        }

        if (RetailMerchandisingKillSwitch::enableCategoryAttributesAndCategoryAttributeGroups())
        {
            ecoResCategoryAttributeGroupTabPage.visible(true);
        }

    }

    // <GEEPL>
    pkWiUTabPage.visible(ecoResCategoryHierarchy.isVisiblePKWiUCode());
    // </GEEPL>

    if (isConfigurationkeyEnabled(configurationKeyNum(ForeignTrade)))
    {
        if (EcoResCategoryHierarchyRole::existRoleHierarchyPair(EcoResCategoryNamedHierarchyRole::Commodity, ecoResCategoryHierarchy.RecId))
        {
            ForeignTradeTabPage.visible(true);
        }
    }
}

