/// <summary>
/// sets the controls state enabled/disabled
/// </summary>
void setFormElementState()
{
    void setTabGroupsState(boolean _status)
    {
        DetailsHeader.enabled(_status);
        HierarchyDetails.enabled(_status);
        EcoResCategoryAttributeCategoryTabPage.enabled(_status);
        CategoryCommodityCodeTabPage.enabled(_status);
        CategoryPagesTabPage.enabled(_status);
    }

    categoryActionPane.enabled(true);

    if (ctrlFormTree.getRoot() == 0)
    {
        commandButtonActivate.enabled(false);
        commandButtonDeactivate.enabled(false);
        categoryFindReferenceGroup.enabled(false);
        ecoResTranslation.enabled(false);
        setTabGroupsState(false);
    }
    else
    {
        categoryFindReferenceGroup.enabled(true);
        ecoResTranslation.enabled(true);
        setTabGroupsState(true);

        if (ecoResCategory.IsActive)
        {
            commandButtonActivate.enabled(false);
            commandButtonDeactivate.enabled(true);
        }
        else
        {
            commandButtonActivate.enabled(true);
            commandButtonDeactivate.enabled(false);
        }
    }
}

