public int task(int _taskId)
{
    #Task

    int ret;

    switch (_taskId)
    {
        case #taskNew:
            this.taskNewRecord();
            break;
        case #taskDeleteRecord:
            this.taskDeleteRecord();
            break;
        default:
            ret = super(_taskId);
    }

    return ret;
}

private void taskNewRecord()
{
    EcoResCategory erCategory;

    #define.EcoResParentCategoryName('@SYS134268')

    if (hierarchyTree && ctrlFormTree.getRoot() != 0)
    {
        hierarchyTree.create();
    }
    else
    {
        erCategory = EcoResCategory::createHierarchyRoot(currentSelectedCategoryHierarchy.RecId, #EcoResParentCategoryName);
        if (erCategory)
        {
            ecoResCategoryTranslation.clear();
            EcoResCategoryTranslation::createOrUpdateCategoryTranslation(erCategory.RecId, erCategory.Name, "", "");
        }
        element.initializeHelper();
    }
    element.rename();
}

private void taskDeleteRecord()
{
    if (hierarchyTree)
    {
        hierarchyTree.delete();
    }

    element.setFormElementState();
}

