}

public void selectionChanged()
{
    super();

    // NOTE: if EcoResCategoryAttribute isn't joined to EcoResCategory table, selecting a diff node triggers EcoResCategory_ds.selectionChanged but none of
    // - Tree's selectionChanged
    // - executeQuery of AC_ds|C_ds
    // - linkActive of AC_ds|C_ds
    // And when DS.selectionChanged does get executed, it's called AFTER DS.executeQuery

    categoryFindReferenceGroup.value(ecoResCategory.RecId);

    element.refreshCategoryAttributeListGrid();
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        element.refreshCategoryAttributeGroupListGrid();
    }
    element.setInheritAttributesState();
    element.refreshCategoryCommodityCodeGrid();
    element.refreshCategoryPagesGrid();
    element.refreshProductCategoryGrid();

    // to maintain the category page record selection, if there is a category page selected at the caller (form ProcCategoryHierarchyManagement's  category page dropdown)
    if (isFromProcCatPageJumpRef)
    {
        procCategoryPage_ds.positionToRecord(catPageSelectedRecord);
    }
}

