/// <summary>
/// Override this method to setup the sort fields.
/// </summary>
public void init()
{
    super();

    // If feature is turn on, then set the sort field use DisplayOrder, by default, the feature is turn off.
    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        QueryBuildDataSource productCategory_ds = this.query().dataSourceTable(tableNum(EcoResProductCategory));
        productCategory_ds.sortClear();
        productCategory_ds.addSortField(fieldNum(EcoResProductCategory, DisplayOrder), SortOrder::Ascending);
        productCategory_ds.addSortField(fieldNum(EcoResProductCategory, Product), SortOrder::Ascending);
        productCategory_ds.addRange(fieldNum(EcoResProductCategory, Category));
    }
}

/// <summary>
/// Override the write method to change all the DisplayOrder values for records shared same <Product, CategoryHierarchy>
/// This is because we need to keep the DisplayOrder value consitent when one of them is changed
/// </summary>
public void write()
{
    if (!ecoResProductCategory.isDisplayOrderUpdated())
    {
        super();
        return;
    }

    boolean isProductAddedToHierarchyMutipleTimes = ecoResProductCategory.isProductAddedToHierarchyMutipleTimes();

    // Show the DialogBox only when feature is On, and DisplayOrder value is updated (No need when other fields are updated).
    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()) && isProductAddedToHierarchyMutipleTimes)
    {
        // "Updating display order value for product '{product number} - {product name}' will update its display order value to the new value across all categories in this hierarchy. Do you want to continue?"
        str dialogText = strFmt(
            "@Retail:RetailUpdateAssociatedProductDisplayOrderValuesReminder",
            ecoResProduct.DisplayProductNumber,
            ecoResProductTranslation.Name
            );

        DialogButton response = Box::yesNo(dialogText, DialogButton::No);

        if (DialogButton::No == response)
        {
            // If No is clicked, refresh and return;
            this.reread();
            return;
        }
    }

    // If feature is not enabled, or feature is enabled, but customer select YES in the DialogBox, do the update.
    // Even when the feature is not enabled, we need to update all the associated values, or there will be incosistent.
    super();

    // Update DisplayOrder value of all the instances of the product in the category hierarchy if current DisplayOrder value is changed.
    if (isProductAddedToHierarchyMutipleTimes)
    {
        ecoResProductCategory.updateDisplayOrderForAllInstances();
    }
}

