/// <summary>
/// Add a new option for product document attachment in office menu.
/// </summary>
/// <param name = "_menuOptions">Office menu option for a form.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(dataentityviewstr(EcoResProductDocumentAttachmentEntity), ExportProductDocumentAttachmentToExcelOptionId);
    exportOption.displayName("@SCM:EcoResProductDocumentAttachmentEntityLabel");
    exportOptions.addEnd(exportOption);
}

/// <summary>
/// Add filter for export option in the office menu for product document attachment entity.
/// </summary>
/// <param name = "_menuItem">Export menu item in the office menu.</param>
/// <returns>Data entity context needed for exporting to excel.</returns>
public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportProductDocumentAttachmentToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(EcoResProductDocumentAttachmentEntity));

            var productDocumentAttachmentEntityInfo = context.findEntityByName(dataentityviewstr(EcoResProductDocumentAttachmentEntity));

            var productDocumentAttachmentFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(EcoResProductDocumentAttachmentEntity));
            var productDocumentAttachmentIdFilter = productDocumentAttachmentFilterBuilder.areEqual(fieldStr(EcoResProductDocumentAttachmentEntity, ProductNumber), EcoResProduct.DisplayProductNumber);

            productDocumentAttachmentEntityInfo.filter(productDocumentAttachmentIdFilter);
            break;
    }

    return context;
}

/// <summary>
/// Adds a set of metadata related to an inventory dimension.
/// </summary>
/// <param name = "_inventDimFieldId">A dimensions field Id in the <c>InventDim</c> table.</param>
/// <param name = "_productMasterRetailGroupFieldId">A dimension retail group field id in the <c>EcoResProductMaster</c> table.</param>
public void addInventDimensionMetadata(
    FieldId _inventDimFieldId,
    FieldId _productMasterRetailGroupFieldId = 0)
{
    if (_productMasterRetailGroupFieldId)
    {
        inventDimFieldIdProductMasterRetailGroupFieldId.insert(_inventDimFieldId, _productMasterRetailGroupFieldId);
    }
}

private void enableCategoryHierarchy()
{
    if (element.args() &&
        element.args().parm() &&
        element.args().menuItemName() == menuitemDisplayStr(RetailProductsByCategory))
    {
        fromProductsByCategory = true;

        if(!RetailSkipDisablingEcoResProductDatasourceFlight::instance().isEnabled())
        {
            // disable product query until a category is selected
            ecoResProduct_ds.query().dataSourceTable(tableNum(EcoResProduct)).enabled(false);
        }

        categoryHierarchyLookup.visible(true);
        categoryTree.visible(true);
        verticalSplitter.visible(true);
        this.initializeCategoryHierarchy();
        this.initializeCategoryTree();
        MCRModifyCategory.visible(true);
    }
    else
    {
        fromProductsByCategory = false;
        EcoResCategory_DS.queryBuildDataSource().enabled(false);
        ecoResProductCategory_ds.queryBuildDataSource().enabled(false);
        ecoResCategoryDescendents_ds.queryBuildDataSource().enabled(false);
    }
}

/// <summary>
///  Visible if current invent item is a warranty product; Invisible if not.
/// </summary>
private void setWarrantyVisible()
{
    boolean isWarrantyVisible = WarrantyFeatureExposure::isWarrantyFeatureEnabled() &&
        (EcoResProduct.ServiceType == EcoResProductServiceType::Warranty);
    Warranty.visible(isWarrantyVisible);
}

/// <summary>
/// Edit method to toggle number nomenclature on or off.
/// </summary>
/// <param name = "_set">Determines if a new value should be set.</param>
/// <param name = "_value">The value to set.</param>
/// <returns>True if nomenclature is used< otherwise, false.</returns>
/// <remarks>This method is needed since the form change group mode is not set to ImplicitInnerOuter.</remarks>
public edit NoYes editUseNomenclature(boolean _set, NoYes _value)
{
    if (_set)
    {
        if (_value)
        {
            EcoResProductMasterProductVariantNomenclatureId_DS.create();

            EcoResProductMasterProductVariantNomenclatureId.ProductMaster = EcoResProduct.RecId;
            EcoResProductMasterProductVariantNomenclatureId.NomenclatureRole = EcoResNomenclatureRole::Id;

            EcoResProductMasterProductVariantNomenclatureId_DS.allowEdit(true);
        }
        else
        {
            EcoResProductMasterProductVariantNomenclatureId_DS.delete();

            EcoResProduct_ds.research(true);
        }
        return _value;
    }
    else
    {
        return (EcoResProductMasterProductVariantNomenclatureId.ProductMaster != 0);
    }
}

