void setGroupfieldsEnabled()
{
    Enumerator  productDimensionsEnumerator = InventProductDimension::getEnumeratorForEnabledDimensions();

    boolean productIsReleased = ecoResProduct.isReleased();

    while (productDimensionsEnumerator.moveNext())
    {
        InventProductDimension productDimension = productDimensionsEnumerator.current();

        if (!inventDimFieldIdProductMasterRetailGroupFieldId.exists(productDimension.parmInventDimFieldId()))
        {
            continue;
        }

        FieldId productMasterRetailGroupFieldId = inventDimFieldIdProductMasterRetailGroupFieldId.lookup(productDimension.parmInventDimFieldId());

        ecoResProduct_EcoResProductMaster_ds.object(productMasterRetailGroupFieldId).allowEdit(!productIsReleased && productDimension.isActiveForProductMaster(ecoResProduct.RecId));
    }
}

public void setProductModelingPolicyEnabled()
{
    boolean     isProductMaster                              = ecoResProduct.isProductMaster();
    boolean     allowEditIsAutomaticVariantGenerationEnabled = isProductMaster && ecoResProduct_EcoResProductMaster.isPredefinedVariants();
    boolean     allowEditIsReuseExistingConfigurationEnabled = isProductMaster && ecoResProduct_EcoResProductMaster.isDimensionBased();

    ecoResProductMasterModelingPolicy_ds.object(fieldNum(EcoResProductMasterModelingPolicy, IsVariantGenerationEnabled)).allowEdit(allowEditIsAutomaticVariantGenerationEnabled);
    ecoResProductMasterModelingPolicy_ds.object(fieldNum(EcoResProductMasterModelingPolicy, IsVariantGenerationEnabled)).skip(!allowEditIsAutomaticVariantGenerationEnabled);
    ecoResProductMasterModelingPolicy_ds.object(fieldNum(EcoResProductMasterModelingPolicy, IsReuseConfigurationEnabled)).allowEdit(allowEditIsReuseExistingConfigurationEnabled);
    ecoResProductMasterModelingPolicy_ds.object(fieldNum(EcoResProductMasterModelingPolicy, IsReuseConfigurationEnabled)).skip(!allowEditIsReuseExistingConfigurationEnabled);
}

private void setUseNomenclatureCheckBoxesEnabled()
{
    boolean isProductMaster = ecoResProduct.isProductMaster();
    UseNomenclatureCheckBox.enabled(isProductMaster);
    UseNomenclatureNameCheckbox.enabled(isProductMaster);
}

