public void close()
{
    super();

    // refresh the caller form EcoResProductServiceWarranty data source.
    if (WarrantyFeatureExposure::isWarrantyFeatureEnabled() &&
        EcoResProduct.ServiceType == EcoResProductServiceType::Warranty)
    {
        // Need to keep the as to avoid cast error.
        WarrantyIProductInfoDisplayer displayer = element.args().caller() as WarrantyIProductInfoDisplayer;

        if (displayer)
        {
            displayer.refreshWarrantyProductInfo();
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    ecoResProduct_ds.object(fieldNum(EcoResProduct, PdsCWProduct)).allowEdit(!ecoResProduct.isReleased());
    ecoResProduct_ds.object(fieldNum(EcoResProduct, PdsCWProduct)).skip(!ecoResProduct.isReleased());

    EcoResProductMasterProductVariantNomenclatureId_DS.allowEdit(EcoResProductMasterProductVariantNomenclatureId.ProductMaster != 0);
    EcoResProductMasterProductVariantNomenclatureName_DS.allowEdit(EcoResProductMasterProductVariantNomenclatureName.ProductMaster != 0);

    element.setUseNomenclatureCheckBoxesEnabled();

    element.setWarrantyVisible();

    return ret;
}

