public void selectionChanged()
{
    CaseCategoryHierarchyDetail caseCategoryHierarchyDetail;

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        element.initializeRetailKitOptions();
    }

    element.setProductModelingPolicyEnabled();
    element.setGroupfieldsEnabled();

    // Engineering Change
    select firstOnly RecId from caseCategoryHierarchyDetail
                           where caseCategoryHierarchyDetail.CategoryType == CaseCategoryType::EngineeringChange;

    engineeringChangeGroupCreateCase.enabled(caseCategoryHierarchyDetail.RecId);
    engineeringChangeGroupAssociateCase.enabled(caseCategoryHierarchyDetail.RecId);
    engineeringChangeGroupAddToLog.enabled(caseCategoryHierarchyDetail.RecId);
}

public void init()
{
    QueryBuildDataSource    qbds;
    QueryBuildRange         queryBuildRange;
    Range                   range;
    Args                    callerArgs;

    super();

    qbds = this.query().dataSourceName(this.name());

    // Filter to show only product masters or distinct products. Don't show variants
    queryBuildRange = qbds.addRange(fieldNum(EcoResProduct, InstanceRelationType));
    if (element.args() && element.args().caller() && element.args().caller().name() == formStr(EcoResProductListPage))
    {
        // Only show records in the grid which are also shown in the caller
        callerArgs = element.args().caller().args();
        if (callerArgs && callerArgs.parmEnumType() == enumNum(EcoResProductListPage))
        {
            switch (callerArgs.parmEnum())
            {
                case EcoResProductListPage::ProductMasters:
                    range = queryValue(DictTable::getRelationTypeFromTableName(tableStr(EcoResProductMaster)));
                    break;
                case EcoResProductListPage::DistinctProducts:
                    range = queryValue(DictTable::getRelationTypeFromTableName(tableStr(EcoResDistinctProduct)));
                    break;
                default:
                    break;
            }
        }
    }

    if (range == '')
    {
        range = queryValue(DictTable::getRelationTypeFromTableName(tableStr(EcoResDistinctProduct)));
        range = queryRangeConcat(range, DictTable::getRelationTypeFromTableName(tableStr(EcoResProductMaster)));
    }
    queryBuildRange.value(range);
    queryBuildRange.status(RangeStatus::Hidden);
}

