public boolean validateWrite()
{
    boolean ret = true;

    if (!ecoResProductTranslation.Product || !ecoResProductTranslation.LanguageId)
    {
        ecoResProductTranslation.Product      = ecoResProduct.RecId;        //Initialize in order to avoid validateWrite to fail.
        ecoResProductTranslation.setDefaultLanguage();                      //Initialize in order to avoid validateWrite to fail.
    }

    if (ecoResProductTranslation.canMaintainTranslation())
    {
        ret = super();
    }

    return ret;
}

public void init()
{
    super();

    EcoResProductTranslation::queryAddDefaultLanguage(this.query());
}

public void write()
{
    boolean mustUpdateBuffer = !ecoResProductTranslation.canMaintainTranslation();

    super();

    // after the record will be deleted in the update table method, the data source should be reread
    if (mustUpdateBuffer)
    {
        ecoResProductTranslation.reread();
    }
}

public void modified()
{
    super();

    if (ecoResProductTranslation.Name)
    {
        ecoResProduct.setSearchName(ecoResProductTranslation.Name);
    }
}

/// <summary>
///    Persists a record in the database when super is called.
/// </summary>
/// <remarks>
/// Some products don't have a transportation codes record
/// so call write method and not super and it will update database properly
/// </remarks>
public void write()
{
    if (whsEcoResProductTransportationCodes.RecId == 0)
    {
        whsEcoResProductTransportationCodes.initFromEcoResProduct(ecoResProduct.RecId);
    }

    whsEcoResProductTransportationCodes.write();
}

