}

public void selectionChanged()
{
    super();

    // clear the ranges on EcoResCategoryDescendents data source
    if (fromProductsByCategory && (lastEcoResCategoryRecId != EcoResCategory.RecId)
        && (EcoResCategory.RecId == mHierarchyTree.selectedData()
        || !RetailProductsByCategoryFormsFilterCorrectRecordsFlight::instance().isEnabled()))
    {
        if(!RetailSkipDisablingEcoResProductDatasourceFlight::instance().isEnabled())
        {
            ecoResProduct_ds.query().dataSourceTable(tableNum(EcoResProduct)).enabled(true);
        }

        RetailCategoryHelper::categorySelectionChanged(
            ecoResCategory,
            ecoResCategoryDescendents_ds,
            retailCategoryContainmentLookup_ds,
            selectedHierachyIsSpecial);

        ecoresproduct_ds.executeQuery();

        lastEcoResCategoryRecId = EcoResCategory.RecId;

    }
}

