void clicked()
{
    EcoResProduct           ecoResProductLocal;
    EcoResProductCategory   ecoResProductLocalCategory;
    boolean                 wasDeleted = false;

    for (ecoResProductLocal = EcoResProduct_ds.getFirst(true) ? EcoResProduct_ds.getFirst(true): EcoResProduct_ds.cursor();  ecoResProductLocal;  ecoResProductLocal = EcoResProduct_ds.getnext())
    {
        ecoResProductLocalCategory = EcoResProductCategory::findByProductIdAndCategoryId(ecoResProductLocal.RecId, EcoResCategory.RecId, true);
        if (ecoResProductLocalCategory)
        {
            ttsBegin;
            if (ecoResProductLocalCategory.validateDelete())
            {
                ecoResProductLocalCategory.delete();
            }
            else
            {
                throw error("@SYS104318");
            }
            ttsCommit;
            wasDeleted = true;
        }
    }
    if (wasDeleted)
    {
        EcoResProduct_ds.executeQuery();
    }
    else if (EcoResProduct)
    {
        info("@MCR39485");
    }
}

