/// <summary>
/// Determines if the shelf life dates are specified.
/// </summary>
/// <returns>true if the shelf life dates are specified.</returns>
protected boolean areShelfLifeDatesSpecified()
{
    return inventTable.areShelfLifeDatesSpecified();
}

/// <summary>
/// Resets shelf life dates.
/// </summary>
protected void resetShelfLifeDates()
{
    inventTable.resetShelfLifeDates();
}

/// <summary>
/// Initializes maps with dimensions metadata that the form uses.
/// </summary>
protected void initInventDimensionsMetadataEntries()
{
    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventSizeId),
        fieldNum(InventTable, AltInventSizeId),
        fieldNum(InventTable, StandardInventSizeId));

    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventColorId),
        fieldNum(InventTable, AltInventColorId),
        fieldNum(InventTable, StandardInventColorId));

    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventStyleId),
        fieldNum(InventTable, AltInventStyleId),
        fieldNum(InventTable, StandardInventStyleId));

    this.addInventDimensionMetadata(
        fieldNum(InventDim, ConfigId),
        fieldNum(InventTable, AltConfigId),
        fieldNum(InventTable, StandardConfigId));

    if (isConfigurationkeyEnabled(configurationKeyNum(EcoResProductVersion)))
    {
        this.addInventDimensionMetadata(
            fieldNum(InventDim, InventVersionId),
            fieldNum(InventTable, AltInventVersionId),
            fieldNum(InventTable, StandardInventVersionId));
    }
}

/// <summary>
/// Adds a set of metadata related to an inventory dimension.
/// </summary>
/// <param name = "_inventDimFieldId">The dimensions field Id in InventDim table.</param>
/// <param name = "_altInventTableFieldId">The alternative dimension value field id in the <c>InventTable</c> table.</param>
/// <param name = "_standardInventTableFieldId">The standard dimension value field id in the <c>InventTable</c> table.</param>
public void addInventDimensionMetadata(
    FieldId _inventDimFieldId,
    FieldId _altInventTableFieldId,
    FieldId _standardInventTableFieldId)
{
    inventDimFieldIdInventTableAltFieldId.insert(_inventDimFieldId, _altInventTableFieldId);
    inventDimFieldIdInventTableStandardFieldId.insert(_inventDimFieldId, _standardInventTableFieldId);
}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    InventTable_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public Common docCursor()
{
    return inventTable;
}

/// <summary>
/// Find the newly created record and set it as context.
/// </summary>
/// <param name = "_inventTableRecord">The record to set as context.</param>
internal void setContext(InventTable _inventTableRecord)
{
    // Lookup the specified item by using the lookup record on the InventTable table.
    element.args().lookupRecord(_inventTableRecord);

    InventTable_ds.executeQuery();
}

