}

/// <summary>
/// Enables or disables FiscalInformation_BR_CNPJ_BR control for editing
/// </summary>
public void enableCNPJ()
{
    if (InventTable.ScaleIndicator_BR == ScaleIndicator_BR::NotRelevant)
    {
        FiscalInformation_BR_CNPJ_BR.enabled(true);
    }
    else
    {
        FiscalInformation_BR_CNPJ_BR.enabled(false);
        InventTable.CNPJ_BR = '';
    }
}

/// <summary>
/// Disable NoManualDiscountAllowed checkbox when NoDiscountAllowed is Yes.
/// NoManualDiscountAllowed is a subset option of NoDiscountAllowed. When NoDiscountAllowed is Yes, NoManualDiscountAllowed must match and should not be edited.
/// </summary>
public void enableManualDiscountCheckBox()
{
    boolean shouldEnableManualDiscountAllowedCheckBox = RetailInventTable.noDiscountAllowed != NoYes::Yes;
    POSTerminal_noManualDiscountAllowed.enabled(shouldEnableManualDiscountAllowedCheckBox);
}

