/// <summary>
///  Visible if current invent item is a warranty product; Invisible if not.
/// </summary>
private void setWarrantyVisible()
{
    boolean isWarrantyVisible = WarrantyFeatureExposure::isWarrantyFeatureEnabled() &&
        (EcoResProduct.ServiceType == EcoResProductServiceType::Warranty);
    TabPageWarranty.visible(isWarrantyVisible);
}

private void setAllowEditFields(FormDataSource _dataSource, container _fieldIds, boolean _allowEdit)
{
    for (int i = 1; i <= conLen(_fieldIds); i++)
    {
        this.setAllowEditField(_dataSource, conPeek(_fieldIds, i), _allowEdit);
    }
}

/// <summary>
/// Sets whether the field is allowed to be edited and skipped for the given form field.
/// </summary>
/// <param name = "_dataSource">The data source.</param>
/// <param name = "_fieldId">The field to set the edit properties.</param>
/// <param name = "_allowEdit">A boolean value specifying whether the edit is allowed.</param>
protected void setAllowEditField(FormDataSource _dataSource, FieldId _fieldId, boolean _allowEdit)
{
    var fieldObject = _dataSource.object(_fieldId);
    fieldObject.allowEdit(_allowEdit);
    fieldObject.skip(!_allowEdit);
}

private void enableCategoryHierarchy()
{
    if (element.args() &&
        element.args().parm() &&
        element.args().menuItemName() == menuitemDisplayStr(RetailReleasedProductsByCategory))
    {
        fromReleasedProductsByCategory = true;

        if(!RetailSkipDisablingEcoResProductDatasourceFlight::instance().isEnabled())
        {
            // disable product query until a category is selected
            ecoResProduct_ds.query().dataSourceTable(tableNum(EcoResProduct)).enabled(false);
        }

        categoryHierarchyLookup.visible(true);
        categoryTree.visible(true);
        verticalSplitter.visible(true);
        this.initializeCategoryHierarchy();
        this.initializeCategoryTree();
        mcrModifyCategory.visible(true);
    }
    else if (this.doCallerHaveCategoryFilters() == false) //if category filter is passed then don't disable EcoResCategory datasources...
    {
        fromReleasedProductsByCategory = false;
        ecoResCategory_DS.queryBuildDataSource().enabled(false);
        ecoResProductCategory_ds.queryBuildDataSource().enabled(false);
        ecoResCategoryDescendents_ds.queryBuildDataSource().enabled(false);
        retailCategoryContainmentLookup_ds.queryBuildDataSource().enabled(false);
    }
}

/// <summary>
/// Return True or false based on whether caller has Category filter. We check this based on Enum Type RetailFiltersForReleasedProducts::Category.
/// </summary>
/// <returns>true if caller has Category filters; otherwise, false.</returns>
private boolean doCallerHaveCategoryFilters()
{
    boolean isCallerCategoryFilters;

    if (element.args() &&
        element.args().parmEnumType() == enumNum(RetailFiltersForReleasedProducts) &&
        element.args().parmEnum() == RetailFiltersForReleasedProducts::Category)
    {
        isCallerCategoryFilters = true;
    }
    else
    {
        isCallerCategoryFilters = false;
    }

    return isCallerCategoryFilters;
}

