        tabWHS.visible(false);
        tabTMS.visible(false);
        GeneralAdministration.visible(false);
        CostPosting.visible(false);
        CostCosting.visible(false);
        CostBasePrice.visible(false);

        // Product action pane tab button groups
        ButtonGroupDefineModify.visible(false);
        ButtonGroupDefineNew.visible(false);
