public void enableServiceFields_BR()
{
    boolean enable = false;

    enable = inventTable.ItemType == ItemType::Service;

    inventTable_ds.object(fieldNum(InventTable, icmsOnService_BR)).enabled(enable);
    inventTable_ds.object(fieldNum(InventTable, TaxServiceCode_BR)).enabled(enable);
    inventTable_ds.object(fieldNum(InventTable, TaxFiscalClassification_BR)).enabled(!enable);
    inventTable_ds.object(fieldNum(InventTable, ExceptionCode_BR)).enabled(!enable);

    inventTable_ds.refresh();
}

private EcoResCategoryHierarchy getCategoryHierarchy()
{
    return EcoResCategoryHierarchy::find(categoryHierarchyReferenceGroup.value());
}

display Price getComparisonPrice()
{
    if (EcoResProductUnitConverter::isProductMasterWithProductVariantConversionEnabled(inventTable.ItemId))
    {
        var productUnitConverter = EcoResProductUnitConverter::newForReleasedProduct(
            inventTable.ItemId,
            InventDim::inventDimIdBlank(),
            UnitOfMeasure::unitOfMeasureIdBySymbol(retailInventTable.BaseComparisonUnitCode),
            UnitOfMeasure::unitOfMeasureIdBySymbol(sales.UnitId),
            NoYes::Yes,
            NoYes::No);

        if (productUnitConverter.canValueBeConverted())
        {
            return productUnitConverter.convertValue(sales.price);
        }

        return sales.Price;
    }

    return UnitOfMeasureConverter::convert(sales.Price,
                                           UnitOfMeasure::unitOfMeasureIdBySymbol(retailInventTable.BaseComparisonUnitCode),
                                           UnitOfMeasure::unitOfMeasureIdBySymbol(sales.UnitId),
                                           NoYes::Yes,
                                           InventTable::itemProduct(inventTable.ItemId),
                                           NoYes::No);
}

private boolean hasTemplate()
{
    SysRecordTemplateStorageCompany     company;
    SysRecordTemplateStorageUser        user;

    company = SysRecordTemplateStorage::newCommon(inventTable, SysRecordTemplateType::Company);
    user = SysRecordTemplateStorage::newCommon(inventTable, SysRecordTemplateType::User);

    return (user.get() != conNull() || company.get() != conNull());
}

private boolean showNavigationList()
{
    return (this.args().menuItemName() == menuitemDisplayStr(CostProductDetailsList)
         || this.args().menuItemName() == menuitemDisplayStr(CostReleasedProductsMissingActiveCost)
         || this.args().menuItemName() == menuitemDisplayStr(CostReleasedProductsMissingCostingData));
}

public void init()
{
    pmfFormCtrl = PmfFormCtrl_EcoResProductDetailsExtended::newFromForm(this);
    pmfFormCtrl.initPre();

    super();

    this.setupFallbackProductTranslation();
    this.initInventDimensionsMetadataEntries();

    // hides the navigation list by default
    navigationList.visible(this.showNavigationList());

    pmfFormCtrl.setDatasources(inventTable);
    pmfFormCtrl.initPost();
    releasedProduct = EcoResReleasedProductFactory::construct().getReleasedProductFromProductForm(this);

    this.setApplyTemplateMenuItem();
    this.setUseInvoiceMatchingItemEnabled();
    this.setQualityManagementMenuItemsVisibility();

    this.enableCostingViewMode();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
    }

    FormButtonGroupControl productImagesButton = element.control(element.controlId(formControlStr(EcoResProductDetailsExtended, ProductImagesGroup)));
    productImagesButton.visible(FeatureStateProvider::isFeatureEnabled(OmniChannelMediaManagementAuthoringFeature::instance()));

    element.enableCategoryHierarchy();

    // <GIN>
    isIsoIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    if (isIsoIN)
    {
        taxExciseTypeController = TaxExciseTypeController_IN::construct(null,
                                                                        ExciseManufacture_IN,
                                                                        null,
                                                                        null,
                                                                        null,
                                                                        null,
                                                                        inventTable_DS);
        taxExciseTypeController.parmFieldNumDSA(fieldNum(inventTable, DSA_IN));
        taxExciseTypeController.parmFieldNumExciseRecordType(fieldNum(inventTable, ExciseRecordType_IN));
        taxExciseTypeController.parmFieldNumTariffCodes(fieldNum(inventTable, ExciseTariffCodes_IN));
        element.updateDesign_IN();
    }
    // </GIN>
    boolean taxEngineEnabled = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();

    InventTable_ds.object(fieldNum(InventTable, TaxRateType)).visible(TaxIntegrationUtils::isTaxInformationEnabled());

    InventTable_MiscChargePerKg.visible(FeatureStateProvider::isFeatureEnabled(IntrastatMiscChargesPerKgFeature::instance()));

    this.setRetailInventoryAvailabilityBufferVisible(true);
    this.setRetailInventoryAvailabilityLevelProfileVisible(true);

    FiscalInformation_BR_TaxBurden_V2.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]));

    // If Enable Project Operations on Dynamics 365 Customer Engagement feature is on, Project cost related menu item buttons should be hidden
    if (ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        ProjCostControlAction.visible(false);
        CostControlCommittedCostAction.visible(false);
    }

    Blocking_blockedOnSelfCheckout.visible(FeatureStateProvider::isFeatureEnabled(RetailSelfCheckoutFeature::instance()));
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslation_Name_InList)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslation_NameGrid)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslation_Name)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslation_Description)));

    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslationFallbackView_Name_InList)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslationFallbackView_NameGrid)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslationFallbackView_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductDetailsExtended, EcoResProductTranslationFallbackView_Description)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResProduct_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

