/// <summary>
/// Provides a lookup for the <c>CustomsTariffCodeTable_IN</c> table.
/// </summary>
/// <param name="_formReferenceControl">
/// The form reference control object with which to perform the lookup.
/// </param>
/// <param name="_direction">
/// The Direction_IN string to filter Export/Import tariffCodes.
/// </param>
/// <param name="_companyId">
/// The CompanyId to filter CustomsTariffCodeTable_IN records.
/// </param>
/// <returns>
/// The selected <c>CustomsTariffCodeTable_IN</c> records.
/// </returns>
public void lookupDirectionImportExport(
    FormStringControl _formStringControl,
    Direction_IN      _direction,
    CompanyId         _companyId = curext())
{
    Query                   query          = new Query();
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(CustomsTariffCodeTable_IN), _formStringControl);

    query.allowCrossCompany(true);
    query.addCompanyRange(_companyId);
    sysTableLookup.addLookupfield(fieldNum(CustomsTariffCodeTable_IN, TariffCode));
    sysTableLookup.addLookupfield(fieldNum(CustomsTariffCodeTable_IN, Name));
    QueryBuildDataSource  queryBuildDataSource = query.addDataSource(tableNum(CustomsTariffCodeTable_IN));
    QueryBuildRange       queryBuildRange      = queryBuildDataSource.addRange(fieldNum(CustomsTariffCodeTable_IN, Direction));
    queryBuildRange.value(queryValue(_direction));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Determines whether conversion can be performed between two specified units of measure for product variants.
/// </summary>
/// <param name = "_fromUnitOfMeasureSymbol">The unit of measure to convert from.</param>
/// <param name = "_toUnitOfMeasureSymbol">The unit of measure symbol to convert to.</param>
/// <returns>true if conversion can be performed between two units of measure; otherwise, false.</returns>
private boolean unitsForProductVariantCanBeConverted(
    UnitOfMeasureSymbol _fromUnitOfMeasureSymbol,
    UnitOfMeasureSymbol _toUnitOfMeasureSymbol)
{
    if (EcoResProductUnitConverter::mustProductVariantsHaveSpecificConversion(inventTable.ItemId))
    {
        return inventTable.existUnitConversionForProduct(_fromUnitOfMeasureSymbol, _toUnitOfMeasureSymbol);
    }
    else
    {
        return EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(
                    inventTable.ItemId,
                    '',
                    _fromUnitOfMeasureSymbol,
                    _toUnitOfMeasureSymbol);
    }
}

