/// <summary>
/// Edits manufacturer excise record type.
/// </summary>
/// <param name="_set">
/// True to set.
/// </param>
/// <param name="_exciseManufacture_IN">
/// Manufacturer excise record type.
/// </param>
/// <returns>
/// Manufacturer excise record type.
/// </returns>
public edit ExciseManufacture_IN exciseManufacture_IN(
    boolean                 _set,
    ExciseManufacture_IN    _exciseManufacture_IN)
{
    ExciseManufacture_IN    exciseManufacture;

    inventTable.ExciseRecordType_IN = TaxExciseTypeController_IN::exciseManufacture(
        _set,
        _exciseManufacture_IN,
        inventTable.ExciseRecordType_IN);

    exciseManufacture = taxExciseTypeController.exciseRecordType2ExciseManufacture(inventTable.ExciseRecordType_IN);

    if (_set)
    {
        if (inventTable.ExciseRecordType_IN != ExciseRecordType_IN::None)
        {
            inventTable.DSA_IN = false;
        }

        // update UI field InventTable_ExciseTariffCodes_IN and InventTable_DSA_IN
        element.updateDesign_IN();
    }

    return exciseManufacture;
}

public void init()
{
    super();

    isTemplate = SysRecordTemplate::isTemplateEditFormRecord(inventTable);

    if (element.args() && element.args().record())
    {
        QueryBuildDataSource qbs = this.query().dataSourceTable(tableNum(InventTable));

        switch (element.args().dataset())
        {
            case tableNum(PdsComdPricingTemplateLine):
                // Remove the dynalink created by the kernel between InventTable.ItemId and InventTable.PmfPlanningItemId
                qbs.clearDynalinks();
                PdsComdPricingTemplateLine pdsComdPricingTemplateLine = element.args().record();
                SysQuery::findOrCreateRange(qbs, fieldNum(InventTable,ItemId)).value(queryValue(pdsComdPricingTemplateLine.MarginItemRelation));
                break;

            case tableNum(InventTable):
                // in cases where dynalinks were not created (manual form launch)
                if (!element.args().caller())
                {
                    qbs.clearDynalinks();
                    InventTable refInventTable = element.args().record();
                    SysQuery::findOrCreateRange(qbs, fieldNum(InventTable,ItemId)).value(queryValue(refInventTable.ItemId));
                }
                break;
        }
    }

    // DimensionEntryControl should be disabled in child companies for single record sharing table, 
    // in the same way SegmentEntryControl is working. This fix temporarily address the platform issue.
    SysDataSharingPolicy policy = SysDataSharingPolicy::findSharingPolicyByCompanyAndTable(curExt(), tableId2name(inventTable.TableId));
    if (policy.RecId && policy.IsEnabled && policy.IsSingleDataSharingPolicy && policy.MasterCompany != curExt())
    {
        DimensionEntryControl.allowEdit(false);
        DimensionEntryControl.loadAttributeValueSet(0);
    }
}

public int active()
{
    int ret = super();

    if (pmfFormCtrl)
    {
        pmfFormCtrl.dataSourceActivePost();
    }

    element.setPmfObjects();

    element.setTMSInventEnabledEnabled();

    element.setWarrantyVisible();

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        element.setServiceCodeEnabled_IN();
        element.setCustomsTariffCodeEnabled_IN();
        element.updateDesign_IN();
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        TransferPrice_IN.visible(true);
    }
    // </GIN>
    if (BrazilParameters::isEnabled())
    {
        element.enableServiceFields_BR();
        element.enableButtonFci_BR();
    }

    element.mcrEnableButtons();

    if (RetailParameters::find().GiftcardItem == inventTable.ItemId)
    {
        mcrInventTable_ds.object(fieldNum(MCRInventTable, ShipAlone)).enabled(false);
    }

    mcrInventTable_ds.object(fieldNum(MCRInventTable, DropShipment)).enabled(true);
    mcrInventTable_ds.object(fieldNum(MCRInventTable, DefaultDropShipmentWarehouse)).enabled(true);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]))
    {
        element.enableCNPJ();
    }

    RetailInventTable_ds.research();

    if (isInventTableDisallowProductLifecycleEditingFlightEnabled)
    {
        this.object(fieldNum(InventTable, ProductLifecycleStateId)).allowEdit(InventTable.allowEditProductLifecycleStateId());
    }

    return ret;
}

void write()
{
    WHSPhysDimUOM       whsPhysDimUOM;
    EcoResProductMaster ecoResProductMaster;
    EcoResProductLifecycleStateId prevLifecycleStateId = InventTable.orig().ProductLifecycleStateId;
    boolean updateVariantLifecycleState;

    if (prevLifecycleStateId != inventTable.ProductLifecycleStateId && EcoResProduct.isProductMaster())
    {
        if (Box::yesNo(strFmt("@SCM:ProductLifecycleStateChangeVariantPropagation", inventTable.ProductLifecycleStateId, prevLifecycleStateId), DialogButton::Yes) == DialogButton::Yes)
        {
            updateVariantLifecycleState = true;
        }
    }

    ttsbegin;

    releasedProduct.checkAndUpdateNonStockedDependencies();

    if (#PmfEnabled)
    {
        if (InventTable.PmfProductType == PmfProductType::Formula && ecoResProduct.isProductMaster())
        {
            ecoResProductMaster = ecoResProduct as EcoResProductMaster;
            if (ecoResProductMaster.VariantConfigurationTechnology == EcoResVariantConfigurationTechnologyType::ConstraintBased)
            {
                throw error("@SCM:FormulaNotAllowedForConstraintProduct");
            }
        }
    }

    super();

    if (!mcrInventTable.RecId)
    {
        // need to force the write in the create case since the form won't do it.
        mcrInventTable.InventTable = inventTable.RecId;
        mcrInventTable.write();
    }

    if (updateVariantLifecycleState)
    {
        EcoResProductLifecycleStateUpdater::updateStateOnVariantsInSandbox(InventTable.ItemId, prevLifecycleStateId, inventtable.ProductLifecycleStateId);
    }

    ttscommit;

    // update sales price
    sales_ds.reread();
}

boolean validateWrite()
{
    boolean ret = true;

    if (#PmfEnabled && !isPmfProductTypeChangeCheckHandled)
    {
        ret = ret & this.checkPmfProductTypeChange();
        isPmfProductTypeChangeCheckHandled = true;
    }

    if (!RetailMerchandisingKillSwitch::disableInsertionOfRetailInventTableWhileUpdatingReleasedProduct() && isConfigurationkeyEnabled(configurationKeyNum(Retail)) && InventTable.RecId)
    {
        if (!RetailInventTable)
        {
            RetailInventTable.initFromInventTable(InventTable);
            RetailInventTable.insert();
        }
    }

    ret = ret & super();

    if (ret && inventTable.isProductVariantUnitConversionEnabled())
    {
        ret = inventTable.validateProductVariantUnitConversion(invent.UnitId, purch.UnitId, sales.UnitId);
    }

    if (   inventModelGroupItem.ModelGroupId
        && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU])
        && !inventModelGroupItem.modelGroup().inventModelTypeSecCur_RU().validateWriteInventTable(inventTable))
    {
        return false;
    }

    if (#PmfEnabled)
    {
        if (    (inventTable.orig().PmfProductType  == PmfProductType::None)
            &&  (inventTable.PmfProductType         !=  PmfProductType::None)
            &&  (inventModelGroupItem.ModelGroupId  && (!inventModelGroupItem.modelGroup().isStocked())))
        {
            ret = checkFailed("@PDS1440");
            inventTable.PmfProductType = PmfProductType::None;
        }
    }

    releasedProduct.checkAndUpdateNonStockedDependencies();
    ret = ret && releasedProduct.validateWrite();

    if (ret && InventTableVerifyShelfLifeDaysWarningFlight::instance().isEnabled())
    {
        if (inventTable.PdsShelfAdvice > 99999)
        {
            ret = checkFailed(strFmt("@SCM:VerifyShelfLifeDaysWarning", fieldPName(InventTable, PdsShelfAdvice)));
        }

        if (inventTable.PdsBestBefore > 99999)
        {
            ret = checkFailed(strFmt("@SCM:VerifyShelfLifeDaysWarning", fieldPName(InventTable, PdsBestBefore)));
        }

        if (inventTable.PdsShelfLife > 99999)
        {
            ret = checkFailed(strFmt("@SCM:VerifyShelfLifeDaysWarning", fieldPName(InventTable, PdsShelfLife)));
        }
    }

    return ret;
}

public void selectionChanged()
{
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        element.initializeRetailKitOptions();
        element.enableManualDiscountCheckBox();
        element.enablePeriodicDiscountCheckBox();
        element.enableTenderDiscountCheckBox();
    }

    element.setWHSMenuItems();
    element.setProductDimensionFields();
    element.setCostFields();
    element.setFiscalLIFOFields();
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        element.setAssetGroupIdFields_RU();
        element.setMarkupCodeField_RU();
    }
    element.setProductDimensionGroupField();
    element.setProductModelingPolicyFields();

    element.setItemStockedDependentObjects();
    element.setPDSPotencyFields();

    element.setCatchWeightFields();
}

public boolean checkPmfProductTypeChange()
{
    mustDeleteBOMVersions = false;
    mustRemoveBOMVersionApprovals = false;

    InventTable orig = inventTable.orig();

    boolean ret = true;

    if (inventTable.ItemType != ItemType::Item
        || orig.PmfProductType == inventTable.PmfProductType
        || !BOMVersion::existsForItem(inventTable.ItemId))
    {
        return true;
    }

    if (inventTable.mustDeleteFormulaVersionsBeforeChangingProductionType())
    {
        var dialogResult = Box::okCancel("@PRO2634", DialogButton::Cancel, "@PRO2605", "@PRO2622");
        mustDeleteBOMVersions = dialogResult == DialogButton::Ok;
        ret = dialogResult != DialogButton::Cancel;
    }
    else if (inventTable.mustDeleteBOMVersionsBeforeChangingProductionType())
    {
        var dialogResult = Box::okCancel("@PRO2635", DialogButton::Cancel, "@PRO2608", "@PRO2621");
        mustDeleteBOMVersions = dialogResult == DialogButton::Ok;
        ret = dialogResult != DialogButton::Cancel;
    }
    else if (inventTable.mustRemoveFormulaVersionsApprovalBeforeChangingProductionType())
    {
        var dialogResult = Box::yesNoCancel("@PRO2901", DialogButton::Cancel, "@PRO2602", "@PRO2623");
        mustRemoveBOMVersionApprovals = dialogResult == DialogButton::Yes;
        mustDeleteBOMVersions = dialogResult == DialogButton::No;
        ret = dialogResult != DialogButton::Cancel;
    }

    if (!ret)
    {
        inventTable.PmfProductType = orig.PmfProductType;
        pmfFormulaPlanning_PmfProductType.update();

        inventTable.PmfYieldPct = orig.PmfYieldPct;
        pmfYield_PmfYieldPct.update();

        element.setPmfObjects();
    }

    return ret;
}

/// <summary>
/// Validate the product is assorted or not while delete.
/// </summary>
/// <returns>
/// True in case product is assorted; otherwise, false.
/// </returns>
public boolean validateDelete()
{
    RetailAssortmentLookupExploded retailAsrtmntLookup;
    boolean ret;

    ret = super();

    if (ret && isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        // Fetch record if product about to delete is assorted.
        select * from retailAsrtmntLookup
        where retailAsrtmntLookup.Product == EcoResProduct.RecId;

        // If record exist, product is assorted and throws warning message.
        if (retailAsrtmntLookup)
        {
            ret = checkFailed("@SYS191391");
        }
    }

    return ret;
}

/// <summary>
/// Research the current datasource
/// </summary>
/// <param name = "_retainPosition">true if the record position should be retained; otherwise, false.</param>
/// <remarks>
/// When the fallback translation feature is enabled, a view is added as datasource of this form. This prevents the research to retain the position
/// properly, as the platform currently does not support views as datasources with regards to this scenario. For this reason, the positioning is explicitly
/// implemented here.
/// </remarks>
public void research(boolean _retainPosition = false)
{
    boolean forceRetainPosition = _retainPosition && EcoResProductTranslationFallbackViewModel::retainPositionOnResearch();
    RefRecId currentCursorRecId;

    if (forceRetainPosition)
    {
        currentCursorRecId = InventTable_ds.cursor().RecId;
    }

    super(_retainPosition);

    if (forceRetainPosition)
    {
        InventTable_ds.positionToRecordByValue(fieldNum(InventTable, RecId), int642Str(currentCursorRecId));
    }
}

