public void lookup(FormControl _formControl, str _filterStr)
{
    if (IntrastatStatProc::isRelationByCommodityEnabled())
    {
        Query query = new Query();
        QueryBuildDataSource queryBuildDataSource = query.addDataSource(tablenum(IntrastatStatProc));
        queryBuildDataSource.addRange(fieldnum(IntrastatStatProc, IntrastatCommodity_CZ)).value(queryValue(InventTable.IntrastatCommodity));

        SysTableLookup sysTableLookup = SysTableLookup::newParameters(tablenum(IntrastatStatProc), _formControl);
        sysTableLookup.addLookupfield(fieldnum(IntrastatStatProc, StatProcId), true);
        sysTableLookup.addLookupfield(fieldnum(IntrastatStatProc, Description1));
        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

