public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]))
        {
            if (inventTable.ItemType != ItemType::Service)
            {
                purch_ds.object(fieldNum(InventTableModule, TaxWithholdCalculate_TH)).allowEdit(false);
                purchaseTaxation_TaxWithholdItemGroupHeading_TH.allowEdit(false);
            }
            else
            {
                purch_ds.object(fieldNum(InventTableModule, TaxWithholdCalculate_TH)).allowEdit(true);
                purchaseTaxation_TaxWithholdItemGroupHeading_TH.allowEdit(purch.TaxWithholdCalculate_TH);
            }
        }
        else if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoSA, #isoGB, #isoIE])
            || TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled()
            || TaxWithholdingGlobalFeature::isFeatureAndParamEnabled())
        {
            purch_ds.object(fieldNum(InventTableModule, TaxWithholdCalculate_TH)).allowEdit(true);
            purchaseTaxation_TaxWithholdItemGroupHeading_TH.allowEdit(purch.TaxWithholdCalculate_TH);
        }
    }
    return ret;
}

