/// <summary>
/// Edits Relief category
/// Add this method to overcome maximum number of joins in the datasources query, TFS 3278480
/// </summary>
/// <param name="_set">
/// True to set.
/// </param>
/// <param name="_inventTableModule">
/// InventTableModule datasource.
/// </param>
/// <param name="_newReliefCategoryId_MY">
/// New relief category to set.
/// </param>
/// <returns>
/// Relief category id.
/// </returns>
public edit TaxGSTReliefCategoryId_MY editReliefCategory_MY(boolean _set, InventTableModule _inventTableModule, TaxGSTReliefCategoryId_MY _newReliefCategoryId_MY)
{
    TaxGSTReliefCategory_MY taxGSTReliefCategory_MY;

    if (_set)
    {
        if (_newReliefCategoryId_MY)
        {
            select firstOnly RecId from taxGSTReliefCategory_MY
                where TaxGSTReliefCategory_MY.ReliefCategoryId == _newReliefCategoryId_MY;

            if (taxGSTReliefCategory_MY)
            {
                _inventTableModule.TaxGSTReliefCategory_MY = TaxGSTReliefCategory_MY.RecId;
            }
            else
            {
                warning(strFmt("@SYS316757", "@SYP4860821"));
            }

            return _newReliefCategoryId_MY;
        }
        else
        {
            _inventTableModule.TaxGSTReliefCategory_MY = 0;
            return '';
        }
    }
    else
    {
        if (_inventTableModule.TaxGSTReliefCategory_MY)
        {
            select firstOnly ReliefCategoryId from taxGSTReliefCategory_MY
                where TaxGSTReliefCategory_MY.RecId == _inventTableModule.TaxGSTReliefCategory_MY;

            return taxGSTReliefCategory_MY.ReliefCategoryId;
        }
        else
        {
            return '';
        }
    }
}

