public boolean validateWrite()
{
    boolean ret;

    if (WarrantyInventTable.RecId)
    {
        ret = super();
    }
    else
    {
        // Do not block writing if there is no WarrantyInventTable record for the InventTable.
        ret = true;
    }

    return ret;
}

