/// <summary>
/// When NoDiscountAllowed checkbox is modified, enable or disable NoManualDiscountAllowed accordingly.
/// NoManualDiscountAllowed is a subset option of NoDiscountAllowed. When NoDiscountAllowed is Yes, NoManualDiscountAllowed must match and should not be edited.
/// </summary>
public boolean modified()
{
    boolean ret;

    ret = super();

    element.enableManualDiscountCheckBox();
    element.enablePeriodicDiscountCheckBox();
    element.enableTenderDiscountCheckBox();

    return ret;
}

