/// <summary>
/// This form shows product and product variant information. To prevent all users from seeing all tabs
/// we set the tabs to have a 'Needed Permission' higher than read and then use control level
/// permissions on privileges to give access to individual tabs.
/// (All privileges point to the EcoResProductInformationDialog display menu item)
/// </summary>
[Form]
public class EcoResProductInformationDialog extends FormRun
{
    EcoResProductInformationController  controller;
    InventDimCtrl_Frm_OnHand            inventDimSetupOnHand;
    boolean                             inventSumLoaded;
    private InventAvailabilityFormCache inventAvailabilityFormCache;

}

public void init()
{
    if (element.args().parmObject() is EcoResProductInformationController) {
        controller = element.args().parmObject();
    }
    else
    {
        throw error(error::missingParameter(null));
    }

    super();

    this.setupFallbackProductTranslation();
    this.initLayout();
    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductInformationDialog, EcoResProductTranslation_Name)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductInformationDialog, EcoResProductTranslation_Description)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductInformationDialog, EcoResProductTranslationFallbackView_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductInformationDialog, EcoResProductTranslationFallbackView_Description)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            EcoResProductTranslationFallbackView_ds,
                                                                            EcoResProduct_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

private boolean useCatchWeightAvailabilityCache(InventSum _inventSum)
{
    if (!_inventSum.Itemid)
    {
        return false;
    }

    return WHSInventTable::isItemWHSAndCatchWeightEnabled(_inventSum.Itemid);
}

InventDimCtrl_Frm_OnHand inventDimSetupObject()
{
    if (!inventDimSetupOnHand)
    {
        inventDimSetupOnHand = InventDimCtrl_Frm_OnHand::newFromForm(element);
        inventDimSetupOnHand.setValidDataSourceName(InventSumInventDim_ds.name());            
        inventDimSetupOnHand.initFromCaller();            

        // Always shows/group by only site and warehouse
        InventDimParm dimParm = inventDimSetupOnHand.parmDimParmVisibleGrid();
        dimParm.clear();
        dimParm.InventSiteIdFlag = true;
        dimParm.InventLocationIdFlag = true;            
    }
    return inventDimSetupOnHand;
}

