private void initLayout()
{
    if (!controller.isProductVariant())
    {
        ProductDimensionsGroup.visible(false);
    }

    if (controller.purchOrderSettingType() == InventItemOrderSetupCallerType::Default)
    {
        InventItemPurchSetupLocation.visible(false);
    }

    if (!controller.isCWActive())
    {
        InventSum_ds.object(fieldnum(InventSum, PdsCWAvailPhysical)).visible(false);
        InventSum_ds.object(fieldnum(InventSum, PdsCWReservPhysical)).visible(false);
    }
}

public void initDataSourceFromConfig(FormDataSource _formDataSource)
{
    SysDictTable sysDictTable = new SysDictTable(_formDataSource.table());

    if (isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        _formDataSource.allowCheck(NoYes::Yes);
    }
}

/// <summary>
/// Gets the product lifecycle state. 
/// </summary>
public display EcoResProductLifecycleStateId productLifecycleStateId()
{
    return controller.productLifecycleStateId();
}

public void init()
{
    super();

    QueryBuildRange itemIdRange = this.queryBuildDataSource().addRange(fieldnum(InventTable, ItemId));
    itemIdRange.status(RangeStatus::Hidden);
    itemIdRange.value(queryValue(controller.itemId()));
}

public void jumpRef()
{
    Args args = new Args();
    args.lookupRecord(inventTable);

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(EcoResProductDetailsExtended), MenuItemType::Display);
    menuFunction.run(args);
}

