public void executeQuery()
{
    super();
    controller.initializeReqItemTable(reqItemTable_ds);
}

public void init()
{
    element.initDataSourceFromConfig(this);
    super();            
}

public void init()
{
    super();

    if (this.query())
    {
        var enumerator = InventDimMetaDataCache::dimCovDimEnabledFieldList().getEnumerator();

        while (enumerator.moveNext())
        {
            this.queryBuildDataSource().addSortField(enumerator.current(), SortOrder::Ascending);
        }
    }
}

public void init()
{
    super();

    element.inventDimSetupObject().initQueryInventSum(inventSum_ds);
}

public void executeQuery()
{
    element.inventDimSetupObject().modifyQuery(inventSum_ds, InventSumInventDim_ds);

    inventAvailabilityFormCache.refreshCache();
    super();

    inventSumLoaded = true; 

    element.inventDimSetupObject().formSetControls(true);
}

[Hookable(false)]
display public InventQtyAvailPhysical availPhysical(InventSum _inventSum)
{
    return inventAvailabilityFormCache.availabilityFromSumJoinedWithDim(_inventSum, InventSumInventDim_ds).availPhysical();
}

[Hookable(false)]
display public InventQtyReservPhysical reservPhysical(InventSum _inventSum)
{
    return InventAvailabilityFormCache.availabilityFromSumJoinedWithDim(_inventSum, InventSumInventDim_ds).reservPhysical();
}

[Hookable(false)]
display public PdsCWAvailPhysical cwAvailPhysical(InventSum _inventSum)
{
    if (!element.useCatchWeightAvailabilityCache(_inventSum))
    {
        return _inventSum.pdsCWAvailPhysicalCalculated();
    }

    return inventAvailabilityFormCache.cwSpecificAvailabilityFromSumJoinedWithDim(_inventSum, InventSumInventDim_ds).pdsCWAvailPhysical();
}

[Hookable(false)]
display public PdsCWReservPhysical cwReservPhysical(InventSum _inventSum)
{
    if (!element.useCatchWeightAvailabilityCache(_inventSum))
    {
        return _inventSum.PdsCWReservPhysical;
    }

    return inventAvailabilityFormCache.cwSpecificAvailabilityFromSumJoinedWithDim(_inventSum, InventSumInventDim_ds).pdsCWReservPhysical();
}

public void init()
{
    super();

    controller.filterInventDimOnProductDimensions(this.queryBuildDataSource());
}

public void init()
{
    super();

    QueryBuildRange recIdRange = this.queryBuildDataSource().addRange(fieldnum(InventItemInventSetup, RecId));
    recIdRange.status(RangeStatus::Hidden);
    recIdRange.value(queryValue(controller.getInventItemInventSetup().RecId));
}

public void jumpRef()
{
    controller.productJumpRef();
}

public void jumpRef()
{
    controller.bomJumpRef();
}

public void jumpRef()
{
    controller.routeJumpRef();
}

public void pageActivated()
{
    if (!inventSumLoaded)
    {
        inventSum_ds.executeQuery();
    }

    super();
}

public void pageActivated()
{
    super();

    if (!controller.parmPlanningTabDataLoaded())
    {
        reqItemTable_ds.executeQuery();

        controller.parmPlanningTabDataLoaded(true);
    }
}
