[Form]
public class EcoResProductMasterDimension extends FormRun
{
    RefRecId            color_ProductDimensionAttribute;
    RefRecId            style_ProductDimensionAttribute;
    RefRecId            version_ProductDimensionAttribute;
    RefRecId            size_ProductDimensionAttribute;
    RefRecId            config_ProductDimensionAttribute;
    TableId             lookupTableId;
    EcoResProductMaster productMasterCaller;

    private Common      lookupRecord;
    private FormDataSource lookupDataSource;

    private Map         tableIdTabMap                 = new Map(Types::Integer, Types::Class);
    private Map         inventDimFieldIdTabMap        = new Map(Types::Integer, Types::Class);
    private List        inventDimDataSourceList       = new List(Types::Class);
    private List        retailDisplayOrderColumnList  = new List(Types::Class);

    EcoResProductDimensionDataModel ecoResProductDimensionDataModel = EcoResProductDimensionDataModel::getInstance();

    private const int InitialFK = 1;
    private boolean isProductVersionFeatureEnabled = false;

}

/// <summary>
/// Initializes maps with dimensions metadata that the form uses.
/// </summary>
protected void initInventDimensionsMetadataEntries()
{
    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventSizeId),
        tabPageSizes,
        EcoResProductMasterSize_RetailDisplayOrder);

    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventColorId),
        tabPageColors,
        EcoResProductMasterColor_RetailDisplayOrder);

    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventStyleId),
        tabPageStyles,
        EcoResProductMasterStyle_RetailDisplayOrder);

    this.addInventDimensionMetadata(
        fieldNum(InventDim, InventVersionId),
        tabPageProductVersions);

    this.addInventDimensionMetadata(
        fieldNum(InventDim, ConfigId),
        tabPageConfigurations);
}

/// <summary>
/// Returns a value to be used to initialize a field that holds a foreign key relation.
/// </summary>
/// <returns>A foreign key relation initial value.</returns>
public int parmInitialFK()
{
    return InitialFK;
}

/// <summary>
/// Adds a set of metadata related to an inventory dimension.
/// </summary>
/// <param name = "_inventDimFieldId">The dimensions field Id in InventDim table.</param>
/// <param name = "_dimensionTab">The dimension form tab control.</param>
/// <param name = "_dimensionRetailDisplayOrderColumn">The dimension retail form control.</param>
public void addInventDimensionMetadata(
    FieldId _inventDimFieldId,
    FormTabPageControl _dimensionTab,
    FormRealControl _dimensionRetailDisplayOrderColumn = null)
{
    TableId dimensionTableId = ecoResProductDimensionDataModel.inventDimFieldId2DimensionTableId(_inventDimFieldId);
    tableIdTabMap.insert(dimensionTableId, _dimensionTab);
    inventDimFieldIdTabMap.insert(_inventDimFieldId, _dimensionTab);

    TableId masterDimensionTableId = ecoResProductDimensionDataModel.dimensionTableId2MasterDimensionTableId(dimensionTableId);
    inventDimDataSourceList.addEnd(this.findFirstFormDataSource(masterDimensionTableId));

    if (_dimensionRetailDisplayOrderColumn)
    {
        retailDisplayOrderColumnList.addEnd(_dimensionRetailDisplayOrderColumn);
    }
}

public void addProductRange(
    QueryBuildDataSource    _qbds,
    FieldId                 _productFieldId,
    str                     _rangeValue)
{
    QueryBuildRange productRange = SysQuery::findOrCreateRange(_qbds, _productFieldId);
    productRange.status(RangeStatus::Hidden);
    productRange.value(_rangeValue);
}

private void clearLookupValues()
{
    //if the form is called as Go to main table form the kernel requires the lookup table to be the topmost in the query.
    //That is not the case for this form, so we clear the lookup values to avoid an error message from the kernel
    element.args().lookupValue('');
    element.args().lookupField(0);
    element.args().lookupTable(0);
}

public void init()
{
    // If the form is called from SysTranslationList form opened from Color Groups or Size Groups or Style Groups forms.
    // Form should get closed without any action as there will be no product context at that time. 
    if (element.args().caller() && element.args().caller().name() == formStr(SysTranslationList))
    {
        element.run();
        element.close();
    }

    boolean isKit;
    isProductVersionFeatureEnabled = isConfigurationkeyEnabled(configurationKeyNum(EcoResProductVersion));

    color_ProductDimensionAttribute  = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, InventColorId));
    style_ProductDimensionAttribute  = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, InventStyleId));        
    size_ProductDimensionAttribute   = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, InventSizeId));
    config_ProductDimensionAttribute = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, ConfigId));

    if (isProductVersionFeatureEnabled)
    {
        version_ProductDimensionAttribute  = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, InventVersionId));
    }

    EcoResProductDimPerCompanyFormHelper formHelper = EcoResProductDimPerCompanyFormHelper::construct();
    productMasterCaller = formHelper.getProductMasterRec(element.args());
    element.args().record(productMasterCaller);

    // use this to set the active tab
    lookupTableId = element.args().lookupTable();
    str productDimensionValueName;

    productDimensionValueName = element.args().lookupValue();

    element.clearLookupValues();

    super();

    this.setLookupRecordAndDataSource(productDimensionValueName);

    this.initInventDimensionsMetadataEntries();

    isKit = RetailKit::existByProductMaster(productMasterCaller.RecId);

    if (productMasterCaller.isConstraintBased() || isKit)
    {
        // Make the data sources read-only to prevent modification
        var dataSourcesEnumerator = inventDimDataSourceList.getEnumerator();

        while (dataSourcesEnumerator.moveNext())
        {
            element.makeDataSourceReadOnly(dataSourcesEnumerator.current() as FormDataSource);
        }
    }

    // only show display order when Retail is enabled
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        var retailDisplayOrderColumnsEnumerator = retailDisplayOrderColumnList.getEnumerator();

        while (retailDisplayOrderColumnsEnumerator.moveNext())
        {
            retailDisplayOrderColumnsEnumerator.current().visible(true);
        }
    }
}

/// <summary>
/// Sets up the lookup record and datasource based on the original lookup value and table. The resulting lookup record must match
/// one of the root datasources of the form.
/// </summary>
/// <param name = "_originalLookupValue">The original lookup value representing the product dimension value name.</param>
private void setLookupRecordAndDataSource(str _originalLookupValue)
{
    if (_originalLookupValue)
    {
        switch (lookupTableId)
        {
            case tableNum(EcoResColor):
                lookupRecord = EcoResProductMasterColor::find(productMasterCaller.RecId, EcoResColor::findByName(_originalLookupValue).RecId);
                lookupDataSource = EcoResProductMasterColor_ds;
                break;

            case tableNum(EcoResSize):
                lookupRecord = EcoResProductMasterSize::find(productMasterCaller.RecId, EcoResSize::findByName(_originalLookupValue).RecId);
                lookupDataSource = EcoResProductMasterSize_ds;
                break;

            case tableNum(EcoResStyle):
                lookupRecord = EcoResProductMasterStyle::find(productMasterCaller.RecId, EcoResStyle::findByName(_originalLookupValue).RecId);
                lookupDataSource = EcoResProductMasterStyle_ds;
                break;

            case tableNum(EcoResConfiguration):
                lookupRecord = EcoResProductMasterConfiguration::find(productMasterCaller.RecId, EcoResConfiguration::findByName(_originalLookupValue).RecId);
                lookupDataSource = EcoResProductMasterConfiguration_ds;
                break;

            case tableNum(EcoResVersion):
                lookupRecord = EcoResProductMasterVersion::find(productMasterCaller.RecId, EcoResVersion::findByName(_originalLookupValue).RecId);
                lookupDataSource = EcoResProductMasterVersion_ds;
                break;
        }
    }
}

public void initHelperAddRangeLink(QueryBuildDataSource _qbds, fieldID _recId )
{
    _qbds.addRange(fieldNum(EcoResProductMasterDimValueTranslation, LanguageId)).value(queryValue(SystemParameters::getSystemLanguageId()));

    _qbds.addLink(_recId, fieldNum(EcoResProductMasterDimValueTranslation, ProductMasterDimensionValue));
}

/// <summary>
/// Disable create, edit and delete action for specified Data Source.
/// </summary>
/// <param name="_formDataSource">
/// The specified Form Data Source.
/// </param>
protected void makeDataSourceReadOnly(FormDataSource _formDataSource)
{
    _formDataSource.allowCreate(false);
    _formDataSource.allowDelete(false);
}

public void run()
{
    super();
    element.setActiveTabs(productMasterCaller.RecId);
}

public void setActiveTabs(RefRecId _productMaster)
{
    this.setVisibleTabs(_productMaster);

    this.setActiveTab();
}

private void setVisibleTabs(RefRecId _productMaster)
{
    RefRecId                    productDimensionGroup       = EcoResProductDimensionGroupProduct::findByProduct(_productMaster).ProductDimensionGroup;
    EcoResProductDimGroupSetup  ecoResProductDimGroupSetup  = EcoResProductDimGroupSetup::newDimensionGroup(productDimensionGroup);

    var inventDimFieldIdTabMapEnumerator = inventDimFieldIdTabMap.getEnumerator();
    while(inventDimFieldIdTabMapEnumerator.moveNext())
    {
        boolean dimensionActive = ecoResProductDimGroupSetup.getFieldSetup(inventDimFieldIdTabMapEnumerator.currentKey()).isActive();
        FormControl tabControl = inventDimFieldIdTabMapEnumerator.currentValue();
        if (tabControl)
        {
            tabControl.visible(dimensionActive);
        }
    }
}

private void setActiveTab()
{
    if (lookupTableId && tableIdTabMap.exists(lookupTableId))
    {
        var tabToActivate = tableIdTabMap.lookup(lookupTableId);
        if (tabToActivate.visible())
        {
            tabToActivate.activatePage();

            if (lookupRecord
                && lookupDataSource)
            {
                lookupDataSource.positionToRecord(lookupRecord);
            }
        }
    }
}

[SysClientCacheDataMethodAttribute]
public display EcoResProductTitle title()
{
    return productMasterCaller.title();
}

/// <summary>
/// Helper class that is called by validateWrite for the four data sources ProductMasDimValueTranslationXXX
/// </summary>
/// <param name="_productMasterDimValueTranslation">
/// The datasource to do the validateWrite for
/// </param>
/// <param name="_productMasterDimensionValueId">
/// The RefRecId identifying the EcoResProductMasterDimensionValue to which the translations belong
/// </param>
public void validateWriteHelper(
    EcoResProductMasterDimValueTranslation  _productMasterDimValueTranslation,
    EcoResProductMasterDimensionValueId     _productMasterDimensionValueId)
{
    if (_productMasterDimValueTranslation.canMaintainTranslation())
    {
        if (!_productMasterDimValueTranslation.ProductMasterDimensionValue)
        {
            _productMasterDimValueTranslation.ProductMasterDimensionValue = _productMasterDimensionValueId;           //Initialize in order to avoid validateWrite to fail.
        }
        if (!_productMasterDimValueTranslation.LanguageId)
        {
            _productMasterDimValueTranslation.LanguageId                  = SystemParameters::getSystemLanguageId();  //Initialize in order to avoid validateWrite to fail.
        }
    }
}

/// <summary>
///    Helper class that is called by write for the four data sources ProductMasDimValueTranslationXXX
/// </summary>
/// <param name="_productMasterDimValueTranslation">
///    The datasource to do the validateWrite for
/// </param>
/// <param name="_productMasterDimensionValue">
///    The EcoResProductMasterDimensionValue to which the translations belong
/// </param>
/// <param name="_dimValueTranslationFormDataSource">
///    The formdatasource for which write is called
/// </param>
/// <param name="_dimValueFormDataSource">
///    The formdatasource containing the EcoResProductMasterDimensionValue record
/// </param>
public void writeHelper(
    EcoResProductMasterDimValueTranslation  _productMasterDimValueTranslation,
    EcoResProductMasterDimensionValue       _productMasterDimensionValue,
    FormDataSource                          _dimValueTranslationFormDataSource,
    FormDataSource                          _dimValueFormDataSource)
{
    // Create or update a translation if something is there to translate
    if (_productMasterDimValueTranslation.canMaintainTranslation())
    {
        if (_productMasterDimValueTranslation.ProductMasterDimensionValue == InitialFK)
        {
            // Save the product master dimension value to have the recid of it available
            if (_dimValueFormDataSource.validateWrite())
            {
                _dimValueFormDataSource.write();
            }

            _productMasterDimValueTranslation.ProductMasterDimensionValue  = _productMasterDimensionValue.RecId;
        }

        _dimValueTranslationFormDataSource.cursor().write();
    }
}

