public void init()
{
    super();

    element.addProductRange(
        this.query().dataSourceName(this.name()),
        fieldNum(EcoResProductMasterColor, ColorProductDimensionAttribute),
        queryValue(color_ProductDimensionAttribute));

    element.addProductRange(this.query().dataSourceName(this.name()),
        fieldNum(EcoResProductMasterColor, ColorProductMaster),
        queryValue(productMasterCaller.RecId));

    // add sort by display order field.
    this.queryBuildDataSource().addSortField(fieldNum(EcoResProductMasterColor, RetailDisplayOrder));
}

public void write()
{
    EcoResColor ecoResColorLocal;

    ttsbegin;

    ecoResColorLocal = EcoResColor::findByName(ecoResColor.Name);
    if (!ecoResColorLocal.RecId)
    {
        ecoResColorLocal.Name = ecoResColor.Name;
        ecoResColorLocal.insert();
    }

    ecoResProductMasterColor.Color                      = ecoResColorLocal.RecId;

    super();

    if (ecoResColor.RecId != ecoResColorLocal.RecId)
    {
        ecoResColor.data(ecoResColorLocal);
        ecoResColor_ds.setCurrent();
    }

    ttscommit;
}

public void initValue()
{
    super();
    ecoResProductMasterColor.Color                          = InitialFK; //Initialize to a non-zero value in order to avoid validateWrite to fail
    ecoResProductMasterColor.ColorProductDimensionAttribute = color_ProductDimensionAttribute;
}

