    super();

    qbds = this.query().dataSourceTable(tableNum(EcoResProductMasterDimValueTranslation));

    element.initHelperAddRangeLink(qbds, fieldNum(EcoResProductMasterVersion, RecId));
}

/// <summary>
/// Initializes TOCTab.
/// </summary>
protected void initialize()
{
    super();

    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        this.sortTabPageByDisplayOrder();
    }

}

/// <summary>
/// Gets the DisplayOrder value of the tab page. The DisplayOrder values are set for dimensions in the dimension group.
/// </summary>
/// <param name = "_formTabPageControl">Form control of the tab page.</param>
/// <param name = "_productDimensionGroup">The dimension group of the product.</param>
/// <returns>The DisplayOrder of the dimension.</returns>
private real getTabPageDisplayOrder(FormTabPageControl _formTabPageControl, EcoResProductDimensionGroupRecId _productDimensionGroup)
{
    var formTabPageControlDataSource = _formTabPageControl.dataSourceObject();
    TableId masterDimensionTableId = formTabPageControlDataSource.table();
    FieldId dimensionFieldId = EcoResProductDimensionDataModel::getInstance().masterDimensionTableId2InventDimFieldId(masterDimensionTableId);
    EcoResProductDimensionGroupFldSetup ecoResProductDimensionGroupFldSetup = EcoResProductDimensionGroupFldSetup::findByDimensionGroupFieldId(_productDimensionGroup, dimensionFieldId);

    return ecoResProductDimensionGroupFldSetup.DisplayOrder;
}

/// <summary>
/// Sorts the tab pages by their DisplayOrder values of dimensions.
/// </summary>
private void sortTabPageByDisplayOrder()
{
    Map                                 displayOrderTabIdMap                = new Map(Types::Real, Types::Class);
    var                                 productMaster                       = productMasterCaller.RecId;
    EcoResProductDimensionGroupRecId    productDimensionGroup               = EcoResProductDimensionGroupProduct::findByProduct(productMaster).ProductDimensionGroup;
    List                                tabList                             = new List(Types::Class);
    List                                valueTabIdList                      = null;

    tabList.addEnd(TabPageConfigurations);
    tabList.addEnd(TabPageSizes);
    tabList.addEnd(TabPageColors);
    tabList.addEnd(TabPageStyles);
    tabList.addEnd(TabPageProductVersions);

    var tabListEnumerator = tabList.getEnumerator();
    while (tabListEnumerator.moveNext())
    {
        real dimensionDisplayOrder = this.getTabPageDisplayOrder(tabListEnumerator.current(), productDimensionGroup);
        if (!displayOrderTabIdMap.exists(dimensionDisplayOrder))
        {
            displayOrderTabIdMap.insert(dimensionDisplayOrder, new List(Types::Integer));
        }

        valueTabIdList = displayOrderTabIdMap.lookup(dimensionDisplayOrder);
        FormTabPageControl formTabPageControl = tabListEnumerator.current();
        valueTabIdList.addEnd(formTabPageControl.id());
    }

    var displayOrderTabIdMapEnumerator = displayOrderTabIdMap.getEnumerator();
    int prevId = 0;
    while (displayOrderTabIdMapEnumerator.moveNext())
    {
        valueTabIdList = displayOrderTabIdMapEnumerator.currentValue();
        var valueTabIdListEnumerator = valueTabIdList.getEnumerator();
        while (valueTabIdListEnumerator.moveNext())
        {
            int currentId = valueTabIdListEnumerator.current();
            this.moveControl(currentId, prevId);
            prevId = currentId;
        }
    }

}

void clicked()
{
    super();
    EcoResProductMasterConfiguration_DS.research(true);
    EcoResProductMasterConfiguration_DS.research(true);
}

public boolean modified()
{
    boolean             ret;
    EcoResConfiguration configurationLocal;

    ret = super();

    configurationLocal = EcoResConfiguration::findByName(ecoResConfiguration.Name);

    if (configurationLocal.RecId)
    {
        ecoResProductMasterConfiguration.Configuration = configurationLocal.RecId;
    }

    return ret;
}

