public void init()
{
    super();

    // To fetch the default language 
    EcoResProductTranslation::queryAddDefaultLanguage(this.query());
}

void clicked()
{
    MenuFunction menuFunction;
    Args args = new Args();

    args.caller(element);
    args.record(InventTable);

    menuFunction = new MenuFunction(menuitemDisplayStr(CaseListPage), MenuItemType::Display);
    menuFunction.enumTypeParameter(enumNum(CaseListFilter));
    menuFunction.enumParameter(enum2int(CaseListFilter::EngineeringChangeCases));

    menuFunction.run(args);
}
