[Form]
public class EcoResProductRelease extends FormRun
{
    EcoResProductReleaseForm    productReleaseForm;
    FormObservableLink          selectAllObserver;

    private const int selectProductTab        = 1;
    private const int selectProductVariantTab = 2;
    private const int selectCompaniesTab      = 3;
    private const int finishTab               = 4;

}

public void init()
{
    Object caller = element.args().caller();
    if (caller && caller is EcoResProductReleaseForm)
    {
        productReleaseForm  = caller as EcoResProductReleaseForm;
        productReleaseForm.parmProductReleaseFormRun(element);
        productReleaseForm.initWizard();

        super();

        productReleaseForm.parmCheckBoxRunInBatch(RunInBatch);
        productReleaseForm.parmCheckboxShowInfolog(ShowInfologUponErrors);
        productReleaseForm.parmCheckBoxIncludeProductMaster(checkBoxIncludeProductMaster);
        productReleaseForm.parmProductFormDataSource(ecoResProduct_ds);
        productReleaseForm.parmProductMasterFormDataSource(ecoResProductMaster_ds);
        productReleaseForm.parmProductVariantsFormDataSource(ecoResDistinctProductVariant_ds);

        selectAllObserver = new FormObservableLink();
        this.setAuthorizedByCompanyPartVisibility(false);
    }
    else
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }
}

void initTabPage(int _fromPage, int _toPage)
{
    switch (_toPage)
    {
        case selectProductTab:
            productReleaseForm.showOnlyProductMaster(false);
            this.setAuthorizedByCompanyPartVisibility(false);
            break;

        case selectProductVariantTab:
            productReleaseForm.showOnlyProductMaster(true);
            this.setAuthorizedByCompanyPartVisibility(true);
            break;

        case selectCompaniesTab:
            this.setAuthorizedByCompanyPartVisibility(false);
            break;

        case finishTab:
            productReleaseForm.showOnlyProductMaster(false);
            productReleaseForm.syncReleaseProductLegalEntity();
            NumberOfProductsInSession.value(int642int(EcoResReleaseSessionProduct::numberOfProductsInSession(productReleaseForm.parmReleaseSessionRecId())));
            NumberOfLegalEntitiesInSession.value(int642int(EcoResReleaseProductLegalEntity::numberOfDistinctLegalEntitiesInSession(productReleaseForm.parmReleaseSessionRecId())));
            ShowInfologUponErrors.value(true);
            this.setAuthorizedByCompanyPartVisibility(false);
            break;
    }
}

public int active()
{
    productReleaseForm.activeProduct();
    return super();
}

/// <summary>
/// Display method for showing a count of all marked product variants.
/// </summary>
/// <param name = "_ecoResProduct">Buffer to use while calculating the count</param>
/// <returns>The count of all marked product variants.</returns>
public display int markedVariants(EcoResProduct _ecoResProduct)
{
    selectAllObserver.observe();

    return int642int(EcoResReleaseSessionProduct::numberOfProductVariantForAProductMasterInSession(
        productReleaseForm.parmReleaseSessionRecId(),
        _ecoResProduct.RecId));
}

public edit NoYes includeProductVariant(
    boolean                         _set,
    EcoResDistinctProductVariant    _ecoResDistinctProductVariant,
    NoYes                           _selected)
{
    selectAllObserver.observe();
    if ( _set)
    {
        selectAllObserver.markChanged();
    }
    return productReleaseForm.includeProduct(_set, _ecoResDistinctProductVariant.RecId, _selected);
}

