public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);
    element.initTabPage(_fromTab, _toTab);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        productReleaseForm.includeProduct(true, ecoResProduct.RecId, this.value());
    }

    return ret;
}

public void clicked()
{
    super();
    boolean isRunInBatch = RunInBatch.checked();

    ShowInfologUponErrors.allowEdit(!isRunInBatch);
    ShowInfologUponErrors.checked(!isRunInBatch);
}
